(9, 8, 5, 53, 0, '10.48.12.129', 1242898909, 0, 1, 1, 1, 1, '', 'LoadMktData 範例', '<r>Function Name = LoadMktData<br/>\nInput Parameter:<br/>\nCurveID = EOD or FO or MO<br/>\nCcy = TWD or USD<br/>\nIndex = CPBA or TWNDF<br/>\nAsOfDate = Date<br/>\n<br/>\nSample Code:\n<CODE><s>[code]</s>sMKTPOINTLIST MktPointList;\n	sMKTPOINT * MktPoint;\n	// Load Market Data\n	LoadMktData(&amp;MktPointList,CurveID,OutCcy,fxIndex1,sAsOfDate());\n\n	for(int j = 0 ; j&lt; MktPointList.List.ItemsUsed;j++){\n		if ( !(MktPoint = (sMKTPOINT*) sGetListItem( (void*)&amp;MktPointList, j ))) {\n			sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , j);\n			return sERROR;\n		}\n\n		printf(\"Date = %s,Rate = %f\\n\",sGCDate(Buf, MktPoint-&gt;Date),MktPoint-&gt;MidRate);\n	}\nint LoadMktData(sMKTPOINTLIST * MktPointListRt,sCURVE_ID CurveID,sCCY Ccy,sINDEX Index,sIDATE AsOfDate){\n\n	int err;\n	char Buf[100];\n\n	sENTITY * MktDataEnt;\n	sMKTDATA * Mktdata;\n\n	// Init MktPointListRT\n	mSetList(MktPointListRt-&gt;List, sizeof( sMKTPOINT ), offsetof( sMKTPOINTLIST, Data ));\n	if ( sInitList( &amp;MktPointListRt-&gt;List, \"PRESET\" ) ) {\n		delete MktPointListRt;\n		MktPointListRt = NULL;\n		sLogMessage(\"Error Initializing List\", sSERIOUS, 0);\n		exit(sERROR);\n	}\n\n	MktDataEnt = sGetEntityByName( \"MKTDATA\" );\n	if ( sEntityCreate( MktDataEnt, (void **)&amp;Mktdata ))\n	{\n		mLogMessage( \" Error allocating customer structure\" );\n		exit( sERROR );\n	}\n\n	sEntityInit(MktDataEnt, (void *) Mktdata, sINIT );\n\n	// Assign Key Word\n	strcpy(Mktdata-&gt;Id.ID,CurveID.ID);\n	strcpy(Mktdata-&gt;Ccy.Name,Ccy.Name);\n	strcpy(Mktdata-&gt;Index.Name,Index.Name);\n	Mktdata-&gt;AsOfDate = AsOfDate;\n\n	err = sEntityDBRead( MktDataEnt, (void *)Mktdata, \"EQUAL\", sNO_ZERO_REC );\n	if ( err &amp;&amp; ( err != sDB_FAIL ))\n	{\n		mLogMessage( \"Abnormal read termination, cancel read.\" );\n		sEntityDBRead( MktDataEnt, Mktdata, \"CANCEL\", 00 );\n		sEntityFree( MktDataEnt, (void **)&amp;Mktdata, sYES ); \n		return sERROR;\n	}\n	sEntityDBRead( MktDataEnt, Mktdata, \"CANCEL\", 00 );\n\n	// Load From MktSpec\n	sMKTSPECLIST * MktSpecList;\n	sMKTSPEC * MktSpec;\n	MktSpecList = &amp;Mktdata-&gt;MktSpecs;\n\n	sMKTPOINTLIST * MktPointList;\n	sMKTPOINT * MktPoint , NewMktPoint;\n\n	sEntityInit(sGetEntityByName(\"MKTPOINT\"), (void *) &amp;NewMktPoint, sINIT );\n\n	for(int i = 0 ; i&lt; MktSpecList-&gt;List.ItemsUsed;i++){\n		if ( !(MktSpec = (sMKTSPEC*) sGetListItem( (void*)MktSpecList, i ))) {\n			sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , i);\n			return sERROR;\n		}\n		MktPointList = &amp;MktSpec-&gt;Rates;\n\n		for(int j = 0 ; j&lt; MktPointList-&gt;List.ItemsUsed;j++){\n			if ( !(MktPoint = (sMKTPOINT*) sGetListItem( (void*)MktPointList, j ))) {\n				sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , j);\n				return sERROR;\n			}\n			memcpy((void *)&amp;NewMktPoint,(void *)MktPoint,sizeof(sMKTPOINT));\n			sInsertListItem( (void *)MktPointListRt, MktPointListRt-&gt;List.ItemsUsed, (void *)&amp;NewMktPoint );\n		}\n	}\n	return sSUCCESS;\n\n}<e>[/code]</e></CODE></r>', '9f191c08aab01f2130768a53e17c6c48', 0, 'AIA=', '24saed3n', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(10, 9, 5, 53, 0, '10.48.12.129', 1242899013, 0, 1, 1, 1, 1, '', 'Payment Advice Document', '<r>請參考附件\n<ATTACHMENT filename=\"Payment Advice Document 設定.doc\" index=\"0\"><s>[attachment=0]</s>Payment Advice Document 設定.doc<e>[/attachment]</e></ATTACHMENT></r>', 'bc7915a0c39be40b671dab2271f47311', 1, 'AAg=', '1dq1gr4y', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(11, 10, 6, 53, 0, '10.48.12.129', 1242899185, 0, 1, 1, 1, 1, '', '在DataGrid(WinForm)中改變符合指定條件的行顏色的方法', '<t>大家都知道DataGrid控制項通過其屬性TableStyles控制每個對應的DataTable的顯示風格，而每個TableStyle又通過其屬性GridColumnStyles控制每一列的顯示風格，但卻沒有屬性或方法可以直接設置每行資料的顯示風格。這或許是出於使用方法考慮（在綁定資料源前當然不知道有哪些行，行中有哪些資料），或許是出於性能考慮（為每行建立顯示風格索引的代價會比較大），或者是其他考慮（我沒想到的^_^）。但實際工作中這種需求很多，比如“當資源小於某個臨界值就變紅色顯示”之類的。如果可以做到改變行的顏色，應該能滿足這些需求中的大部分。然而按照常規方法，在一列當中，每個單格（即每行）的顯示風格都是相同的。<br/>\n改變DataGrid控制項行顏色的基本技巧就是重寫DataGridColumnStyle的Paint方法，更改其中的foreBrush和backBrush兩個參數，再重新調用基類的Paint方法。當然實際操作中我們不能直接繼承DataGridColumnStyle類，而是要從它的子類DataGridTextBoxColumn，DataGridBoolColumn中繼承（可惜我們不能在這個繼承層次中插入我們自己的類，呵呵）。如下所示：<br/>\n public class  DataGridColoredTextBoxColumn : DataGridTextBoxColumn<br/>\n { <br/>\n  //重載繪製單格的函數,如果該單格所在行符合ColoredView中的條件,就改變前景和背景色<br/>\n  protected override void Paint(System.Drawing.Graphics g, <br/>\n   System.Drawing.Rectangle bounds, System.Windows.Forms.CurrencyManager <br/>\n   source, int rowNum, System.Drawing.Brush backBrush, System.Drawing.Brush <br/>\n   foreBrush, bool alignToRight) <br/>\n  { <br/>\n   /*在這裏加入判斷條件，決定是否更改顏色<br/>\n   if (...)<br/>\n    backBrush = new SolidBrush(...);<br/>\n    foreBrush = new SolidBrush(...);<br/>\n   */<br/>\n   <br/>\n   //調用基類的繪製函數<br/>\n   base.Paint(g, bounds, source, rowNum, backBrush, foreBrush, alignToRight);<br/>\n  } <br/>\n } <br/>\n這個方法本身很好理解，我們再為這個類加上一個設置顏色的公共方法，就可以在使用的時候指定顏色了（這裏的顏色是廣義的概念，對於Brush來說，也可以指定渲染方式）。問題的關鍵在於我們如何設置if裏面的那個條件：我們能看出Paint方法的參數rowNum給出了正在繪製的當前行行號，那麼是否可以通過指定行號來決定改變那一行的顏色呢？答案是否定的。首先，rowNum給出的行號是當前顯示的行號，而我們在外部通過某些條件選出來的往往是DataSource中的行號，由於排序等操作會改變顯示順序，這兩者有可能不一致；其次，我們往往是希望符合一定條件的行變色，而不是特定位置的行變色，如果直接指定行號，就會造成設置的顏色成了DataGrid的背景，有失初衷。<br/>\n既然如此，我們是不是可以通過rowNum直接取出要判斷的那一列的內容，根據它的值來判斷是否變色呢？網上有不少例子都是這麼實現的，作為展示一個技巧，當然已經足夠了，不過實際使用卻不太方便。因為這勢必要求我們把判斷條件（\"age&gt;20\", \"name like \'Jack\'\"之類的）硬編碼在方法中，不夠靈活，如果有多個地方需要設置不同的條件，豈不是要編寫多個類？況且要真正地取出rowNum對應的某一列的值，也不是那麼容易的。<br/>\n要解決上述兩個問題，比較理想的思路就是首先想辦法取出rowNum在其綁定的dataSource中對應的行，再通過公共方法指定要變色的行，比較兩者是否相等，如果相等就按預先設置的Brush更改前景和背景。下面是我找到的一種獲取DataSource中對應rowNum的行的方法，通過獲取一個當前的DataView實現，也許不夠簡潔，不過確實可用：<br/>\n //獲取當前正在顯示的DataView<br/>\n DataView currentView;<br/>\n Type iType = this.DataGridTableStyle.DataGrid.DataSource.GetType();<br/>\n if (iType==typeof(System.Data.DataView) )<br/>\n  currentView = (DataView)(this.DataGridTableStyle.DataGrid.DataSource);<br/>\n else if (iType==typeof(System.Data.DataTable) )<br/>\n  currentView = ((DataTable)(this.DataGridTableStyle.DataGrid.DataSource)).DefaultView;<br/>\n else if (iType==typeof(System.Data.DataSet) )<br/>\n  currentView = ((DataSet)(this.DataGridTableStyle.DataGrid.DataSource)).Tables[this.DataGridTableStyle.MappingName].DefaultView;<br/>\n else if (iType==typeof(System.Data.DataViewManager) )<br/>\n {<br/>\n  DataViewManager viewManager = (DataViewManager)(this.DataGridTableStyle.DataGrid.DataSource);<br/>\n  currentView = viewManager.DataSet.Tables[this.DataGridTableStyle.MappingName].DefaultView;<br/>\n }<br/>\n else<br/>\n { //如果dataSource不符合以上四種類型(應該不會吧^_^),拋出系統錯誤<br/>\n  throw(new SystemException()); <br/>\n }<br/>\n <br/>\n //取出當前行<br/>\n DataRow currentRow = currentView[rowNum].Row;<br/>\n <br/>\n找到當前行後就好辦了，和預先指定的一系列行做比較，就可以知道是不是應該變色，如下所示：<br/>\n //搜索當前行是否在設置行色彩的ColoredView當中<br/>\n DataRow currentRow = currentView[rowNum].Row;<br/>\n foreach (DataRow coloredRow in m_coloredDataRows) //m_coloredDataRows是一個DataRow類型的陣列，是private的成員變數<br/>\n {<br/>\n  if (currentRow == coloredRow) <br/>\n  {<br/>\n   backBrush = m_backBrush;<br/>\n   foreBrush = m_foreBrush; <br/>\n   break;<br/>\n  }<br/>\n }<br/>\n至此，我們終於給出了if的判斷條件，問題也就解決了。為了便於使用，我們還要提供一個公共方法，以及幾個私有成員，來供外界設置行和顏色，該方法可以粗略實現如下：<br/>\n private System.Data.DataRow[] m_coloredDataRows;<br/>\n private System.Drawing.Brush m_foreBrush;<br/>\n private System.Drawing.Brush m_backBrush;<br/>\n //設置行色彩<br/>\n public void SetRowsColor(DataRow[] aDataRows, System.Drawing.Brush aForeBrush, System.Drawing.Brush aBackBrush)<br/>\n {<br/>\n  //設置要改變顏色的行<br/>\n  m_coloredDataRows = aDataRows;<br/>\n  //設置前景色<br/>\n  m_foreBrush = aForeBrush;<br/>\n  //設置背景色<br/>\n  m_backBrush = aBackBrush;<br/>\n }<br/>\n使用這個類的方法很簡單，下面舉一個例子，是用其中dTable是一個DataTable物件，用它的Select方法選出了要變色的行，作為SetRowsColor的實參：<br/>\n //設置列以及每列的行樣式<br/>\n DataGridTableStyle dgTableStyle = new DataGridTableStyle();<br/>\n dgTableStyle.MappingName = dTable.TableName;<br/>\n foreach (DataColumn eachCol in dTable.Columns)<br/>\n {<br/>\n  DataGridColoredTextBoxColumn dbColumnStyle = new DataGridColoredTextBoxColumn();<br/>\n  dbColumnStyle.MappingName = eachCol.ColumnName;<br/>\n  //設置變色行的條件以及前景和背景色<br/>\n  dbColumnStyle.SetRowsColor(dTable.Select(\"age&gt;20\"), new SolidBrush(Color.White), new SolidBrush(Color.BlueViolet));<br/>\n  dgTableStyle.GridColumnStyles.Add(dbColumnStyle);<br/>\n }<br/>\n <br/>\n //添加樣式到dataGrid1<br/>\n dataGrid1.TableStyles.Add(dgTableStyle);<br/>\n <br/>\n當然，如果希望通用型更強，這個類還有很多功能有待完善，比如目前只能設定一組行的顏色，再次設置會替換掉原來的內容，要想設置多組行與顏色的對應，還得添加一些資料結構；還有當前只能設置Brush，實現不了改變字體之類的功能，不過目前我也還沒找到好辦法（Paint方法以及PaintText方法都沒有關於字體的參數）。<br/>\n<br/>\n完整的測試代碼附在下面：<br/>\nusing System.Data;<br/>\npublic class Form2 : System.Windows.Forms.Form<br/>\n{<br/>\n public Form2()<br/>\n {<br/>\n  //<br/>\n  // Windows 表單設計器支援所必需的<br/>\n  //<br/>\n  InitializeComponent();<br/>\n  DataTable dTable;<br/>\n  //創建測試DataTable<br/>\n  dTable = new DataTable(\"person\");<br/>\n  //添加測試列<br/>\n  dTable.Columns.Add(new DataColumn(\"id\",typeof(int)) );<br/>\n  dTable.Columns.Add(new DataColumn(\"name\",typeof(string)) );<br/>\n  dTable.Columns.Add(new DataColumn(\"age\",typeof(int)) );<br/>\n  //添加測試行   <br/>\n  DataRow newDataRow;<br/>\n  Random iRandom = new Random();<br/>\n  for(short i=0;i&lt;20;i++)<br/>\n  {<br/>\n   newDataRow = dTable.NewRow();<br/>\n   newDataRow[0] = i;<br/>\n   newDataRow[1] = Convert.ToString(iRandom.Next(0,15))+\"song\";<br/>\n   newDataRow[2] = iRandom.Next(15,30);<br/>\n   dTable.Rows.Add(newDataRow);<br/>\n  }<br/>\n  //設置列以及每列的行樣式<br/>\n  DataGridTableStyle dgTableStyle = new DataGridTableStyle();<br/>\n  dgTableStyle.MappingName = dTable.TableName;<br/>\n  foreach (DataColumn eachCol in dTable.Columns)<br/>\n  {<br/>\n   DataGridColoredTextBoxColumn dbColumnStyle = new DataGridColoredTextBoxColumn();<br/>\n   dbColumnStyle.MappingName = eachCol.ColumnName;<br/>\n   //設置變色行的條件以及前景和背景色<br/>\n   dbColumnStyle.SetRowsColor(dTable.Select(\"age&gt;20\"), new SolidBrush(Color.White), new SolidBrush(Color.BlueViolet));<br/>\n   dgTableStyle.GridColumnStyles.Add(dbColumnStyle);<br/>\n  }<br/>\n  <br/>\n  //添加樣式到dataGrid1<br/>\n  dataGrid1.TableStyles.Add(dgTableStyle);<br/>\n  <br/>\n  dataGrid1.SetDataBinding(dTable,\"\");  <br/>\n }<br/>\n}<br/>\npublic class  DataGridColoredTextBoxColumn : DataGridTextBoxColumn<br/>\n{ <br/>\n private System.Data.DataRow[] m_coloredDataRows;<br/>\n private System.Drawing.Brush m_foreBrush;<br/>\n private System.Drawing.Brush m_backBrush;<br/>\n //設置行色彩<br/>\n public void SetRowsColor(DataRow[] aDataRows, System.Drawing.Brush aForeBrush, System.Drawing.Brush aBackBrush)<br/>\n {<br/>\n  //設置要改變顏色的行<br/>\n  m_coloredDataRows = aDataRows;<br/>\n  //設置前景色<br/>\n  m_foreBrush = aForeBrush;<br/>\n  //設置背景色<br/>\n  m_backBrush = aBackBrush;<br/>\n }<br/>\n //重載繪製單格的函數,如果該單格所在行符合ColoredView中的條件,就改變前景和背景色<br/>\n protected override void Paint(System.Drawing.Graphics g, <br/>\n  System.Drawing.Rectangle bounds, System.Windows.Forms.CurrencyManager <br/>\n  source, int rowNum, System.Drawing.Brush backBrush, System.Drawing.Brush <br/>\n  foreBrush, bool alignToRight) <br/>\n { <br/>\n  //獲取當前正在顯示的DataView<br/>\n  DataView currentView;<br/>\n  Type iType = this.DataGridTableStyle.DataGrid.DataSource.GetType();<br/>\n  if (iType==typeof(System.Data.DataView) )<br/>\n   currentView = (DataView)(this.DataGridTableStyle.DataGrid.DataSource);<br/>\n  else if (iType==typeof(System.Data.DataTable) )<br/>\n   currentView = ((DataTable)(this.DataGridTableStyle.DataGrid.DataSource)).DefaultView;<br/>\n  else if (iType==typeof(System.Data.DataSet) )<br/>\n   currentView = ((DataSet)(this.DataGridTableStyle.DataGrid.DataSource)).Tables[this.DataGridTableStyle.MappingName].DefaultView;<br/>\n  else if (iType==typeof(System.Data.DataViewManager) )<br/>\n  {<br/>\n   DataViewManager viewManager = (DataViewManager)(this.DataGridTableStyle.DataGrid.DataSource);<br/>\n   currentView = viewManager.DataSet.Tables[this.DataGridTableStyle.MappingName].DefaultView;<br/>\n  }<br/>\n  else<br/>\n  { //如果dataSource不符合以上四種類型(應該不會吧^_^),拋出系統錯誤<br/>\n   throw(new SystemException()); <br/>\n  }<br/>\n  //搜索當前行是否在設置行色彩的ColoredView當中<br/>\n  DataRow currentRow = currentView[rowNum].Row;<br/>\n  foreach (DataRow coloredRow in m_coloredDataRows)<br/>\n  {<br/>\n   if (currentRow == coloredRow) <br/>\n   {<br/>\n    backBrush = m_backBrush;<br/>\n    foreBrush = m_foreBrush; <br/>\n    break;<br/>\n   }<br/>\n  }<br/>\n  //調用基類的繪製函數<br/>\n  base.Paint(g, bounds, source, rowNum, backBrush, foreBrush, alignToRight);<br/>\n } <br/>\n}</t>', '0e079bfa382d551b9961a112d8ff5a92', 0, '', '1sxic5r5', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(12, 11, 5, 53, 0, '10.48.12.129', 1242899724, 0, 1, 1, 1, 1, '', 'How to configure Gateway Loader Server', '<r>1. Add Service\n<ATTACHMENT filename=\"Add Service.jpg\" index=\"6\"><s>[attachment=6]</s>Add Service.jpg<e>[/attachment]</e></ATTACHMENT>\n2. Write Program MAPPING Library<br/>\nSample Code:\n<CODE><s>[code]</s>#include \"stk.h\"\n#include \"valuelist/valuelist.h\"\n\n\nextern \"C\"\n{\n\nmDLLEXPORT int sOpenLdrApiTranslation(const char * ExternalType,\n                                      SU_ValueList *pSrcVL,\n                                      sENTITY **Entity,\n                                      void **Data)\n{\n   cout &lt;&lt; \"\\n###### Enter sOpenLdrApiTranslation()\\n\" &lt;&lt; endl;\n\n   if(strcmp(pSrcVL-&gt;GetEntityName(), \"FX_EXT\"))\n   {\n      SU_CString err(\"Error: External type is not FX_EXT\");\n      sLogMessage(err.Data(), sSERIOUS, 0);\n      throw err;\n   }\n\n   sENTITY *entity = sGetEntityByName(\"FXSPOT\");\n   void *data = NULL;\n\n   if(sEntityCreate(entity, &amp;data) != sSUCCESS)\n   {\n      SU_CString err(\"Error creating entity instance for FXSPOT.\");\n      sLogMessage(err.Data(), sSERIOUS, 0);\n      throw err;\n   }\n   \n   *Entity = entity;\n   *Data = (void *)data;\n\n   sFXTRADE *pFXTrade = (sFXTRADE*) data;\n\n   sFOREX *pForex =  pFXTrade-&gt;Forex;\n\n   strcpy( pForex-&gt;BoughtCcy.Name, pSrcVL-&gt;GetStringValue(\"CCY_BUY\"));\n   strcpy( pForex-&gt;SoldCcy.Name, pSrcVL-&gt;GetStringValue(\"CCY_SELL\"));\n   pForex-&gt;BoughtAmt = pSrcVL-&gt;GetFloatValue(\"BUY_AMOUNT\");\n\n   pForex-&gt;XRate = pSrcVL-&gt;GetFloatValue(\"XRATE\");\n\n   strcpy(pFXTrade-&gt;Env-&gt;Company.Name, pSrcVL-&gt;GetStringValue(\"COMPANY\"));\n   strcpy(pFXTrade-&gt;Env-&gt;Desk.Name,pSrcVL-&gt;GetStringValue(\"DESK\"));\n   strcpy(pFXTrade-&gt;Env-&gt;Book.Name,pSrcVL-&gt;GetStringValue(\"BOOK\"));\n   \n   strcpy(pFXTrade-&gt;Env-&gt;Cust.Name, pSrcVL-&gt;GetStringValue(\"CUSTOMER\"));\n   return sSUCCESS;\n}\n}<e>[/code]</e></CODE>\nSet Gateway Action Mapping Definition\n<ATTACHMENT filename=\"Gateway Action Mapping Definition.jpg\" index=\"5\"><s>[attachment=5]</s>Gateway Action Mapping Definition.jpg<e>[/attachment]</e></ATTACHMENT>\nSet Gateway Loader Function Definition\n<ATTACHMENT filename=\"Gateway Loader Function Definition.jpg\" index=\"4\"><s>[attachment=4]</s>Gateway Loader Function Definition.jpg<e>[/attachment]</e></ATTACHMENT> <br/>\nMethod (On Distributed components Guide)\n<ATTACHMENT filename=\"Methods Supported by the Gateway Load Server.jpg\" index=\"3\"><s>[attachment=3]</s>Methods Supported by the Gateway Load Server.jpg<e>[/attachment]</e></ATTACHMENT>\nFunction Name = ModuleName:FunctionName<br/>\n<br/>\nRestart Gateway Server<br/>\nAdd Adapters on Transaction Blotter and Fail Queue Monitor\n<ATTACHMENT filename=\"Gateway Transaction Blotter.jpg\" index=\"2\"><s>[attachment=2]</s>Gateway Transaction Blotter.jpg<e>[/attachment]</e></ATTACHMENT>\n<ATTACHMENT filename=\"Gateway Fail Queue Monitor.jpg\" index=\"1\"><s>[attachment=1]</s>Gateway Fail Queue Monitor.jpg<e>[/attachment]</e></ATTACHMENT></r>', 'cf8b64ec8db502638a9cefc846371e59', 1, 'AIg=', '1nhjuezl', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(13, 12, 6, 53, 0, '10.48.12.129', 1242901246, 0, 1, 1, 1, 1, '', '主動式監控設定', '<r>ConvMsg.CreateActiveMonitorMessage(arg1,arg2,arg3,arg4,arg5,arg6,arg7)<br/>\n<br/>\narg1=填入在發生狀況時的「系統名稱」<br/>\narg2=填入在發生狀況時的「錯誤訊息」<br/>\narg3=填入在發生狀況時的「來源主機」<br/>\narg4=填入起始監控時間<br/>\narg5=填入結束監控時間<br/>\narg6=填入監控時間「間隔秒數」<br/>\narg7=填入「UserCommand.Register」進行注冊作業<br/>\n<br/>\nActiveMonitorStruct Class<br/>\n宣告回覆監控主機方式<br/>\n<br/>\nActiveMonitor Class<br/>\n啟動監控主機回覆作業<br/>\n<br/>\n\n<CODE><s>[code]</s><i>\n</i>ActiveMonitor SAM;\n        ActiveMonitorStruct AMS;\n// Package Request Active Monitor Message\n                    // 建立訊息轉換物件\n                    ConvertMessage ConvMsg = new ConvertMessage();\n                    // 建立Package封裝物件\n                    SocketPackage CSEM = ConvMsg.CreateActiveMonitorMessage(\"RMDS\", \"主機無回應\", Dns.GetHostName(), DateTime.MinValue, DateTime.MaxValue, 60, UserCommand.Register);\n                    ServiceSocketClient FS = new ServiceSocketClient(Properties.Settings.Default.EventServerIP, Properties.Settings.Default.EventServerPort);\n                    FS.Log_Event += new FEIBLib.LogEventHandler(FS_Log_Event);\n                    SendMarketDataFlag = FS.NoWaitSend(CSEM.SendMessage);\n                    // Define Active Monitor Struct\n                    AMS = new ActiveMonitorStruct();\n                    // Define Application Name\n                    AMS.ServiceName = \"RMDS\";\n                    // Define Monitor Start Time\n                    AMS.StartTime = DateTime.Now;\n                    // Define Monitor End Time\n                    AMS.EndTime = DateTime.MaxValue;\n                    AMS.period = 60;\n                    AMS.MsgServer = Properties.Settings.Default.EventServerIP;\n                    AMS.MsgPort = Properties.Settings.Default.EventServerPort;\n                    // this is Client\n                    AMS.IsServer = false;\n                    AMS.CmdStat = UserCommand.Update;\n                    AMS.Message = \"RMDS主機無回應\";\n                    AMS.HostName = Dns.GetHostName();\n                    AMS.Connect = true;\n                    // Initial Timer \n                    SAM = new ActiveMonitor(AMS);\n                    SAM.Start();\n                    CSEM.Dispose();<e>[/code]</e></CODE></r>', '58d5fbe05e824aee4b48b2caab10da2c', 0, 'AIA=', '36u8nsig', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(14, 13, 5, 53, 0, '10.48.12.129', 1242905283, 0, 1, 1, 1, 1, '', 'RetrievePriceYield', '<r>自動評價Sec<br/>\n輸入： SecId,Yield,Price\n<CODE><s>[code]</s>int RetrievePriceYield(char* secid, double&amp; price, double&amp; yield)\n{\n  sSTATS Stats;\n  sBOND* Trade;\n  sEntityCreate(sGetEntityByName(\"BOND\"), (void**)&amp;Trade);\n  sSEC* passedSec(NULL);\n  sCCY Ccy;\n  strcpy(Ccy.Name, \"TWD\");\n  sSEC_ID SecID;\n  strcpy(SecID.ID, secid);\n  sSetCurveID(\"EOD\");\n  if(sSUCCESS != sFetchSec( &amp;passedSec, (sSECTYPE)0, Ccy, \n                           SecID, 0, 0 ))\n  {\n    mLogMessage(\"Could not find Security\");\n  }\n\n  if (0 == price &amp;&amp; 0 == yield)\n  {\n    // First defaulting to Yield if can be found.\n    yield = passedSec-&gt;Yield;\n    if (0 == yield)\n      price = passedSec-&gt;Price;\n  }\n  sCreateFakeBondTrade(Trade, passedSec, 00);\n\n  sIRATE Spread(0);\n  sUINT StatMask(0);\n  sUINT Mode(0);\n\n   return sBondPrice( &amp;Stats,NULL, Trade,\n							   NULL,&amp;price, &amp;yield,\n	  						   NULL, &amp;Spread,\n							   NULL, NULL,\n							   NULL, NULL, NULL,\n							   NULL,  NULL,\n							   NULL, \n							   NULL, sAsOfDate(),\n							   StatMask, Mode );\n\n}<e>[/code]</e></CODE></r>', '07d0df08c22c90f99fcadd9543f4f126', 0, 'AIA=', '19ktq74p', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(15, 14, 5, 53, 0, '10.48.12.129', 1242905394, 0, 1, 1, 1, 1, '', 'Create FX FWD Trade', '<r>建立一筆FX FWD Trade\n<CODE><s>[code]</s>sENTITY * TradeEntity = sGetEntityByName( \"FXFWD\" );\n	sFXTRADE * Trade = NULL;\n\n	if( sEntityCreate( TradeEntity, (void **) &amp;Trade ) )\n	{\n		mLogMessage( \"Error creating FxSpot Trade\" );\n		printf(\"Error creating FxSpot Trade\");\n		sEntityFree( TradeEntity, (void **)&amp;Trade, sYES );\n	}\n\n	Trade-&gt;Forex-&gt;ValDate = sSybIDate(\"20081017\");\n	strcpy(Trade-&gt;Forex-&gt;BoughtCcy.Name,\"USD\");\n	strcpy(Trade-&gt;Forex-&gt;SoldCcy.Name,\"GBP\");\n	Trade-&gt;Forex-&gt;BoughtAmt = 10000000;\n	Trade-&gt;Forex-&gt;SpotRate = 1.5288;\n	Trade-&gt;Forex-&gt;FwdPoints = -2.13;\n\n\n	// Defaulting the other data on the trade\n	if(sEntityFill( TradeEntity, Trade ) == sERROR)\n	{\n		sEntityFree( TradeEntity, (void **)&amp;Trade, sYES );\n	}\n\n	// Validate the trade\n	sERRMSGLIST messages;\n	sInitList( &amp;messages.List, \"sERRMSG\" );\n	if( sEntityValidate( TradeEntity, Trade, &amp;messages ) )\n	{\n		// Validate Error\n		sEntityFree( TradeEntity, (void **)&amp;Trade, sYES );\n		sManageList( &amp;messages.List, sLIST_FREE, 0 );\n	}\n	// Release Memory\n	sManageList( &amp;messages.List, sLIST_FREE, 0 );<e>[/code]</e></CODE></r>', 'a895374b91f3219ff116ee159f670cdd', 0, 'AIA=', '25pdsmma', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(16, 15, 5, 53, 0, '10.48.12.129', 1242905458, 0, 1, 1, 1, 1, '', 'Create FX SOPT Trade', '<r>建立一筆 FX SOPT Trade\n<CODE><s>[code]</s>	sENTITY * TradeEntity = sGetEntityByName( \"FXSPOT\" );\n	sFXTRADE * Trade = NULL;\n\n	if( sEntityCreate( TradeEntity, (void **) &amp;Trade ) )\n	{\n		mLogMessage( \"Error creating FxSpot Trade\" );\n		printf(\"Error creating FxSpot Trade\");\n		sEntityFree( TradeEntity, (void **)&amp;Trade, sYES );\n	}\n\n	Trade-&gt;Forex-&gt;ValDate = sSybIDate(\"20081014\");\n	strcpy(Trade-&gt;Forex-&gt;BoughtCcy.Name,\"HKD\");\n	strcpy(Trade-&gt;Forex-&gt;SoldCcy.Name,\"USD\");\n	Trade-&gt;Forex-&gt;BoughtAmt = 150000;\n	Trade-&gt;Forex-&gt;XRate = 1.30044;\n	strcpy(Trade-&gt;Env-&gt;Cust.Name,\"STDLHKO\");\n	strcpy(Trade-&gt;Env-&gt;Trader.ID,\"WEBFX\");\n\n	//	sENTITY * FXBKEntity = sGetEntityByName(\"MFXBK_DAT\");\n	//	sFX_BOOKDATA * BookData = NULL;\n	//	if( sEntityCreate( FXBKEntity, (void **) &amp;BookData ) )\n	//	{\n	//		mLogMessage( \"Error creating MFXBK_DAT Entity\" );\n	//		sEntityFree( FXBKEntity, (void **)&amp;BookData, sYES );\n	//	}\n\n\n	//// Insert Book Data\n	//	if (sInsertListItem((sENTITYLIST *)&amp;Trade-&gt;Forex-&gt;BookData,\n	//	Trade-&gt;Forex-&gt;BookData.List.ItemsUsed, (sFX_BOOKDATA *)BookData )) {\n	//		sLogMessage( \"Error inserting %s BookData list\", \n	//			sSERIOUS, \n	//			0, \n	//			NULL );\n	//	}\n\n	// Defaulting the other data on the trade\n	if(sEntityFill( TradeEntity, Trade ) == sERROR)\n	{\n		sEntityFree( TradeEntity, (void **)&amp;Trade, sYES );\n	}\n	strcpy(Trade-&gt;Env-&gt;Book.Name,\"MG\");\n	strcpy(Trade-&gt;Env-&gt;Desk.Name,\"MARGIN_TRADE\");\n	\n	// Fill Book Data\n	sFX_BOOKDATA * BookData = (sFX_BOOKDATA *)Trade-&gt;Forex-&gt;BookData.Data;\n	strcpy(BookData-&gt;Book.Name,\"MG\");\n	strcpy(BookData-&gt;Desk.Name,\"MARGIN_TRADE\");\n\n	// Validate the trade\n	sERRMSGLIST messages;\n	sInitList( &amp;messages.List, \"sERRMSG\" );\n	if( sEntityValidate( TradeEntity, Trade, &amp;messages ) )\n	{\n		// Validate Error\n		sEntityFree( TradeEntity, (void **)&amp;Trade, sYES );\n		sManageList( &amp;messages.List, sLIST_FREE, 0 );\n	}\n	// Release Memory\n	sManageList( &amp;messages.List, sLIST_FREE, 0 );\n\n	if(sEntityDoSTDAction(TradeEntity,Trade,NULL,sACT_DO,00) == sSUCCESS){\n		printf(\"Success\\n\");\n	} else\n	{\n		printf(\"Fail\\n\");\n	}<e>[/code]</e></CODE></r>', 'bcffa18dd2916b616a43a75b6aca3540', 0, 'AIA=', '2zn7cw8r', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(17, 16, 5, 53, 0, '10.48.12.129', 1242905498, 0, 1, 1, 1, 1, '', 'Create BOND Trade', '<r>建立一筆Bond Trade\n<CODE><s>[code]</s>	char ErrorMessage[500];\n	//sSetCurveID(\"EOD\");\n		sENTITY * BondEntity = sGetEntityByName( \"BOND_TR\" );\n		sBOND_TR * BondTrade = NULL;\n\n		if( sEntityCreate( BondEntity, (void **) &amp;BondTrade ) )\n		{\n			// Create Error Message\n			sprintf(ErrorMessage,\"%s\",\"Error Create Entity\");\n\n			sEntityFree( BondEntity, (void **)&amp;BondTrade, sYES );\n		}\n\n		// Security ID\n		strcpy(BondTrade-&gt;Bond-&gt;SecID.ID,\"A97106\");\n\n		// Ccy\n		strcpy(BondTrade-&gt;Bond-&gt;Ccy.Name,\"TWD\");\n\n		// Fetch Sec 會自動填入Sec Notional\n		sSEC* passedSec(NULL);\n		if (sFetchSec( &amp;passedSec, (sSECTYPE)0, BondTrade-&gt;Bond-&gt;Ccy, BondTrade-&gt;Bond-&gt;SecID, 0, 0) != sSUCCESS )\n		{\n			// Create Error Message\n			sprintf(ErrorMessage,\"%s\",\"Fetch Sec Error\");\n		}\n\n		// Create a bond given a security\n		sCreateFakeBondTrade(BondTrade-&gt;Bond, passedSec, 00);\n\n\n		// Trade Date &amp; Trade Entry Date\n		BondTrade-&gt;Env-&gt;TradeDate =  sSybIDate(\"20081030\");\n		BondTrade-&gt;Env-&gt;TradeEntryDate = sSybIDate(\"20081030\");\n\n		// Value Date &amp; Settle Date is T+2\n		BondTrade-&gt;Bond-&gt;ValueDate = sSybIDate(\"20081103\");\n		BondTrade-&gt;Bond-&gt;SettleDate =sSybIDate(\"20081103\");\n		\n		sIDATE asdate = sSybIDate(\"20081030\");\n		// Quantity\n		BondTrade-&gt;Bond-&gt;Notional = 50000000;\n\n		// Bought or Sale\n			//BondTrade-&gt;Bond-&gt;PorS = sPURCHASE;\n			BondTrade-&gt;Bond-&gt;PorS = sSALE;\n\n		// Cpty\n		strcpy(BondTrade-&gt;Env-&gt;Cust.Name,\"BTTW\");\n		// Desk\n		strcpy(BondTrade-&gt;Env-&gt;Desk.Name,\"FIXED_INCOME\");\n		// Book\n		strcpy(BondTrade-&gt;Env-&gt;Book.Name,\"T_FI\");\n		// Trader\n		strcpy(BondTrade-&gt;Env-&gt;Trader.ID,\"FI_GARY\");\n		\n		double Yield;\n		double Price;\n\n		// Yield\n		Yield = 1.95/100;\n		Price = 0.0;\n		BondTrade-&gt;Bond-&gt;Yield = Yield;\n		//BondTrade-&gt;Bond-&gt;Price = Price;\n				// Defaulting the other data on the trade\n		if(sEntityFill( BondEntity, BondTrade ) == sERROR)\n		{\n			// Create Error Message\n			sprintf(ErrorMessage,\"%s\",\"Fill Trade Error\");\n			sEntityFree( BondEntity, (void **)&amp;BondTrade, sYES );\n		}\n		//Yield=0.0;\n		//Price=0.0;\n		sSTATS Stats;\n		sUINT StatMask(0);\n		sUINT Mode(0);\n		sAMOUNT Accure;\n		sAMOUNT SettleAmt;\n		sIRATE Spread = 0.0;\n		sBondPrice( &amp;Stats,NULL, BondTrade-&gt;Bond,\n			NULL, &amp;Price, &amp;Yield,\n			NULL, NULL,\n			&amp;SettleAmt, NULL,\n			&amp;Accure, NULL, NULL,\n			NULL, NULL,\n			NULL, \n			NULL, asdate,\n			StatMask, Mode );\n\n		BondTrade-&gt;Bond-&gt;Yield = Yield;\n		BondTrade-&gt;Bond-&gt;Price = Price;\n		BondTrade-&gt;Bond-&gt;CleanPrice = Price;\n		BondTrade-&gt;Bond-&gt;Settlement = SettleAmt;\n		\n		// Defaulting the other data on the trade\n		if(sEntityFill( BondEntity, BondTrade ) == sERROR)\n		{\n			// Create Error Message\n			sprintf(ErrorMessage,\"%s\",\"Fill Trade Error\");\n			sEntityFree( BondEntity, (void **)&amp;BondTrade, sYES );\n		}\n\n		// Validate the trade\n		sERRMSGLIST messages;\n		sInitList( &amp;messages.List, \"sERRMSG\" );\n		if( sEntityValidate( BondEntity, BondTrade, &amp;messages ) )\n		{\n			// Create Error Message\n			sprintf(ErrorMessage,\"%s\",\"messages.Data-&gt;Message\");\n			sEntityFree( BondEntity, (void **)&amp;BondTrade, sYES );\n			sManageList( &amp;messages.List, sLIST_FREE, 0 );\n<e>[/code]</e></CODE></r>', 'e0a58abf2c2a086fbed9cbdf7885dd48', 0, 'AIA=', '1bt46ipv', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(18, 17, 5, 53, 0, '10.48.12.129', 1242905533, 0, 1, 1, 1, 1, '', 'Create REPO Trade', '<r>Create REPO Trade\n<CODE><s>[code]</s>	char ErrorMessage[1024];\n	bool Flag = true;\n\n	//sSetCurveID(\"EOD\");\n	sENTITY * RepoEntity = sGetEntityByName( \"REPO_TR\" );\n	sREPO_TR * RepoTrade = NULL;\n\n	if( sEntityCreate( RepoEntity, (void **) &amp;RepoTrade ) )\n	{\n		mLogMessage( \"Error creating Repo Trade\" );\n		printf(\"Error creating Repo Trade\");\n		sEntityFree( RepoEntity, (void **)&amp;RepoTrade, sYES );\n	}\n\n	sENTITY * RepoCollEnt = sGetEntityByName(\"COLLTRL\");\n	sCOLLATERAL * RepoColl = NULL;\n	if( sEntityCreate( RepoCollEnt, (void **) &amp;RepoColl ) )\n	{\n		mLogMessage( \"Error creating Repo Collateral\" );\n		printf(\"Error creating Repo Collateral\");\n		sEntityFree( RepoEntity, (void **)&amp;RepoTrade, sYES );\n	}\n	// =========== Repo Trade 必要欄位 =============\n	// Repo Type\n	RepoTrade-&gt;Repo-&gt;RepType = sRP_REPO;\n	// Customer\n	strcpy(RepoTrade-&gt;Env-&gt;Cust.Name,\"BTTW\");\n	// TradeDate\n	RepoTrade-&gt;Env-&gt;TradeDate = sSybIDate(\"20081009\");\n	// TradeEntryDate\n	RepoTrade-&gt;Env-&gt;TradeEntryDate = sSybIDate(\"20081009\");\n	// SecID\n	strcpy(RepoColl-&gt;SecId.ID,\"A97106\");\n	// Secruity Type\n	RepoColl-&gt;SecType = sST_TR_BOND_NOTE;\n	// Collateral Type\n	RepoColl-&gt;CollType = sCT_BOND;\n	// Currency\n	strcpy(RepoColl-&gt;Ccy.Name,\"TWD\");\n	// Quantity\n	RepoColl-&gt;Quantity = 50000000;\n	// StartDate\n	RepoColl-&gt;StartDate = sSybIDate(\"20081009\");\n	// EndDate\n	RepoColl-&gt;EndDate =  sSybIDate(\"20081014\");\n	//RepoColl-&gt;Principal = 50718737;\n\n	//RepoColl-&gt;StartDirtyPrice = 1.01437474;\n	//RepoColl-&gt;EndDirtyPrice = 1.01682036;\n	RepoTrade-&gt;Asset-&gt;Notional = 50718737;\n\n	//RepoTrade-&gt;Asset-&gt;MatDate = sSybIDate(\"20081013\");\n	//RepoTrade-&gt;Asset-&gt;Int.Rate = 0.88;\n\n	strcpy(RepoTrade-&gt;Env-&gt;Book.Name,\"T_FI\");\n	strcpy(RepoTrade-&gt;Env-&gt;Desk.Name,\"FIXED_INCOME\");\n\n	// 取得 Sec 資料\n	if (sFetchSec( &amp;RepoColl-&gt;Sec, (sSECTYPE)0, RepoColl-&gt;Ccy, RepoColl-&gt;SecId, 0, 0) != sSUCCESS )\n	{\n		Flag = false;\n		// Fetch Sec Error\n		sprintf(ErrorMessage,\"%s\",\"Fetch Sec Error\");\n	}\n\n	sInsertListItem( (void *)&amp;RepoTrade-&gt;Repo-&gt;CollList, RepoTrade-&gt;Repo-&gt;CollList.List.ItemsUsed, (void *)RepoColl );\n\n	// Defaulting the other data on the trade\n	if(sEntityFill( RepoEntity, RepoTrade ) == sERROR)\n	{\n		sEntityFree( RepoEntity, (void **)&amp;RepoTrade, sYES );\n\n	}\n	//RepoTrade-&gt;Repo-&gt;CollList.Data-&gt;StartDirtyPrice = 1.01437474;\n	//RepoTrade-&gt;Repo-&gt;CollList.Data-&gt;EndDirtyPrice = 1.01682036;\n\n	// Validate the trade\n	sERRMSGLIST messages;\n	sInitList( &amp;messages.List, \"sERRMSG\" );\n	if( sEntityValidate( RepoEntity, RepoTrade, &amp;messages ) )\n	{\n		Flag = false;\n		// Validate Error\n		sprintf(ErrorMessage,\"%s\",messages.Data-&gt;Message);\n		sEntityFree( RepoEntity, (void **)&amp;RepoTrade, sYES );\n		sManageList( &amp;messages.List, sLIST_FREE, 0 );\n	}\n	// =========== Repo Trade 必要欄位 =============\n\n	if( sEntityDoSTDAction( RepoEntity, RepoTrade, NULL, sACT_DO , 00 ) != sSUCCESS )\n	{\n		printf(\"Fail To Do Save\");\n	}\n<e>[/code]</e></CODE></r>', 'f5e1e6e6e594bc4a8a033f8d6d9de287', 0, 'AIA=', '11e0153h', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(19, 18, 5, 53, 0, '10.48.12.129', 1242905713, 0, 1, 1, 1, 1, '', '利用插補法求利率(InteropRate)', '<r>輸入：CURVEID(EOD,MO,FO)<br/>\n輸入：CURVEDATE <br/>\n輸入：CalDays 計算天數<br/>\n輸入：Ccy<br/>\nSample Code\n<CODE><s>[code]</s><i>\n</i>sCCY OutCcy;\nstrcpy(OutCcy.Name,\"TWD\");\nRate = cGetInteropRate(\"EOD\",sAsOfDate(),5,OutCcy);<e>[/code]</e></CODE>\nFunction\n<CODE><s>[code]</s>sIRATE cGetInteropRate(char * CurveID,sIDATE CurveDate,int CalDays,sCCY Ccy){\n\n	sIRATE RtRate = 0.0;\n	sIDATE CalDate;\n	sIRATE UpRate,DownRate;\n	sIDATE UpDate,DownDate;\n\n	// Init CurveID\n	sSetCurveID(CurveID);\n	// Get Default Index\n	sINDEX fxIndex = sFXIndex(Ccy);\n\n	sCURVELIST * CcyCurve;\n	sCURVE * CurveVal;\n\n	CalDate = sAsOfDate() + CalDays;\n\n	CcyCurve = sGetZeroCurve( fxIndex, Ccy, NULL, ( sBA ) 0, CurveDate,00 );\n	if(CcyCurve == NULL){\n		mLogMessage(\"Generate Zero Curve Error!!\");\n	} else {\n		for(int i = 0 ; i&lt; CcyCurve-&gt;List.ItemsUsed;i++){\n			if ( !(CurveVal = (sCURVE*) sGetListItem( (void*)CcyCurve, i ))) {\n				sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , i);\n				return sERROR;\n			}			\n			UpDate = CurveVal-&gt;Date;\n			UpRate = CurveVal-&gt;Rate;\n\n			if(CurveVal-&gt;Date == CalDate){\n				RtRate = CurveVal-&gt;Rate;\n				break;\n			} else {\n				// Use Interop\n				if(CurveVal-&gt;Date &gt; CalDate &amp;&amp; i &gt; 0){\n					RtRate =  DownRate + ((UpRate-DownRate) * ((double)(CalDate - DownDate) / (double)(UpDate - DownDate)));\n					break;\n				}\n			}\n			DownRate = CurveVal-&gt;Rate;\n			DownDate = CurveVal-&gt;Date;\n		}\n	}\n\n	return RtRate;\n}<e>[/code]</e></CODE></r>', 'c0c29eb9d01fa51067b1b01651042d1a', 0, 'AIA=', '7h7xtc3f', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(20, 19, 5, 53, 0, '10.48.12.129', 1242905767, 0, 1, 1, 1, 1, '', '取得平均利率', '<r>Sample Code\n<CODE><s>[code]</s><i>\n</i>sCCY OutCcy,InCcy;\nsCURVE_ID CurveID;\nstrcpy(CurveID.ID,\"EOD\");\nstrcpy(OutCcy.Name,\"TWD\");\nstrcpy(InCcy.Name,\"USD\");\nRate=cGetAvgRate(CurveID,sCT_FX_RATE,InCcy,OutCcy);<e>[/code]</e></CODE>\nFunction\n<CODE><s>[code]</s>sIRATE cGetAvgRate(sCURVE_ID CurveID,sCOMMTYPE Type,sCCY Ccy1,sCCY Ccy2){\n\n	bool xONFlag = false,TNFlag = false; // 系統會判斷是否有O/N 或 T/N Flag來計算不同的方式\n\n	sIRATE RtRate,ONBid = 0.0, TNBid = 0.0,ONAsk = 0.0, TNAsk = 0.0, SpotBid = 0.0, SpotAsk = 0.0,Ccy1ZeroRate = 0.0,Ccy2ZeroRate = 0.0;\n	char Buf[100];\n	int err;\n	sIDATE AsOfDate = sAsOfDate() ;\n	sGCDate(Buf, AsOfDate);\n\n	sSetCurveID(CurveID.ID);\n\n	sQUOTED * quoted = sQuotedCurrency( &amp;Ccy1, &amp;Ccy2, NULL, 0 );\n	if(sIsBlank(CurveID.ID)){\n		strcpy(CurveID.ID,\"EOD\");\n	}\n	// Get Commset\n	sCOMMDATASET * CommSet;\n	sEntityCreate( sGetEntityByName(\"COMMSET\"), (void **)&amp;CommSet);\n	strcpy(CommSet-&gt;Id.ID,CurveID.ID);\n	CommSet-&gt;Type = Type;\n	CommSet-&gt;AsOfDate = AsOfDate;\n	sprintf(CommSet-&gt;Name.Name,\"%s/%s/%s\",Trim(sICEnum(\"sCOMMTYPE\",CommSet-&gt;Type,sSHORT_ENUM)),quoted-&gt;Ccy1.Name,quoted-&gt;Ccy2.Name);\n	//strcpy(CommSet-&gt;Name.Name,\"FXRATE/EUR/USD\");\n\n	if((err = sGetFXCommData(CommSet,NULL,00)) == sERROR){\n		mLogMessage(\"Error Get\");\n	}\n\n	sCOMMDATA * CommData;\n	for(int i = 0 ; i&lt; CommSet-&gt;DataList.List.ItemsUsed;i++){\n		if ( !(CommData = (sCOMMDATA*) sGetListItem( (void*)&amp;CommSet-&gt;DataList, i ))) {\n			sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , i);\n			return sERROR;\n		}\n		printf(\"Date = %s,MidRate = %f,BidRate = %f, AskRate = %f\\n\",sGCDate(Buf, CommData-&gt;Date),CommData-&gt;Value,CommData-&gt;BidValue,CommData-&gt;AskValue);\n		if(CommData-&gt;Date == 1304) { //O/N Rate\n			ONBid = CommData-&gt;BidValue;\n			ONAsk = CommData-&gt;AskValue;\n			ONFlag = true;\n		}\n		if(CommData-&gt;Date == 1305) { //T/N Rate\n			TNBid = CommData-&gt;BidValue;\n			TNAsk = CommData-&gt;AskValue;\n			TNFlag = true;\n		}\n		if(CommData-&gt;Date == 1306){ //SPOT Rate\n			SpotBid = CommData-&gt;BidValue;\n			SpotAsk = CommData-&gt;AskValue;\n		}\n	}\n	if(ONFlag || TNFlag){\n		// 當有O/N or T/N的計算方式\n		if(quoted-&gt;SpotDays == 2)\n			RtRate = ((SpotAsk - (ONBid / pow(10,quoted-&gt;Representation)) - (TNBid / pow(10,quoted-&gt;Representation))) +\n			(SpotBid - (ONAsk / pow(10,quoted-&gt;Representation)) - (TNAsk / pow(10,quoted-&gt;Representation)))) /2;\n		else\n			RtRate = ((SpotAsk - ONBid / pow(10,quoted-&gt;Representation)) +\n			(SpotBid - ONAsk / pow(10,quoted-&gt;Representation))) /2;\n	} else {\n		// 利用直線插補法來計算\n		// 取得Ccy1的Zero Rate\n\n		sINDEX fxIndex;\n		sCURVELIST * CcyCurve;\n		sCURVE * CurveVal;\n\n		int Day1,Day2;\n		sIRATE Rate1,Rate2;\n		sIDATE SpotDate;\n\n		sCCYDATA * cCCYData;\n		sCALDATA  * cal;\n\n		fxIndex = sFXIndex(Ccy1);\n		CcyCurve = sGetZeroCurve( fxIndex, Ccy1, NULL, ( sBA ) 0, AsOfDate,00 );\n		if(CcyCurve == NULL){\n			mLogMessage(\"Generate Zero Curve Error!!\");\n			return sERROR;\n		}\n		for(int i = 0 ; i&lt; CcyCurve-&gt;List.ItemsUsed;i++){\n			if ( !(CurveVal = (sCURVE*) sGetListItem( (void*)CcyCurve, i ))) {\n				sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , i);\n				return sERROR;\n			}\n			if(i == 0){\n				Day1 =  CurveVal-&gt;Date;\n				Rate1 = CurveVal-&gt;Rate;\n			}\n			if(i == 1){\n				Day2 = CurveVal-&gt;Date;\n				Rate2 = CurveVal-&gt;Rate;\n			}\n		}\n		// 取得SpotDay\n		//新增依Currency來判斷Calendar是否符合Business Day\n		// Get Currency Data\n		cCCYData = sGetCcyData(Ccy1);\n		if ( !( cal = sGetCalData( cCCYData-&gt;Cal ))) {\n			mLogMessage( \"Invalid calendar name\" );\n			exit( sERROR );\n		}\n		SpotDate = AsOfDate + quoted-&gt;SpotDays;\n		SpotDate = sBusDate( SpotDate, cal, sFOLLOWING);\n		// 判斷Day2 是否等於 SpotDate\n		if(Day2 == SpotDate){\n			Ccy1ZeroRate = Rate2;\n		} else {\n			// 直線插補法\n			Ccy1ZeroRate = Rate1 + (Rate2 - Rate1) * ((SpotDate - AsOfDate )/(Day2 - AsOfDate ));\n\n		}\n\n		fxIndex = sFXIndex(Ccy2);\n		CcyCurve = sGetZeroCurve( fxIndex, Ccy2, NULL, ( sBA ) 0, AsOfDate,00 );\n		if(CcyCurve == NULL){\n			mLogMessage(\"Generate Zero Curve Error!!\");\n			return sERROR;\n		}\n		for(int i = 0 ; i&lt; CcyCurve-&gt;List.ItemsUsed;i++){\n			if ( !(CurveVal = (sCURVE*) sGetListItem( (void*)CcyCurve, i ))) {\n				sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , i);\n				return sERROR;\n			}\n			if(i == 0){\n				Day1 =  CurveVal-&gt;Date;\n				Rate1 = CurveVal-&gt;Rate;\n			}\n			if(i == 1){\n				Day2 = CurveVal-&gt;Date;\n				Rate2 = CurveVal-&gt;Rate;\n			}\n		}\n\n		// 取得SpotDay\n		//新增依Currency來判斷Calendar是否符合Business Day\n		// Get Currency Data\n		cCCYData = sGetCcyData(Ccy2);\n		if ( !( cal = sGetCalData( cCCYData-&gt;Cal ))) {\n			mLogMessage( \"Invalid calendar name\" );\n			exit( sERROR );\n		}\n		SpotDate = AsOfDate + quoted-&gt;SpotDays;\n		SpotDate = sBusDate( SpotDate, cal, sFOLLOWING);\n		// 判斷Day2 是否等於 SpotDate\n		if(Day2 == SpotDate){\n			Ccy2ZeroRate = Rate2;\n		} else {\n			// 直線插補法\n			Ccy2ZeroRate = Rate1 + (Rate2 - Rate1) * (SpotDate - Day1) / (Day2 - Day1);\n\n		}\n		RtRate = ((SpotBid + SpotAsk)/2) * exp((Ccy2ZeroRate - Ccy1ZeroRate) * (SpotDate - AsOfDate) / 365 * -1);\n	}\n\n	return RtRate;\n<e>[/code]</e></CODE></r>', '95dcbb149b855d9e3dd7f2b8b980656a', 0, 'AIA=', '2vamkq2f', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(21, 20, 5, 53, 0, '10.48.12.129', 1242905794, 0, 1, 1, 1, 1, '', '將字串去空白', '<r><CODE><s>[code]</s>char * Trim(char* str1){\n	// Remove All Space\n	char *tempstr = new char[20];\n	int i; \n	int n; \n	int length; \n\n	n=0; \n	while (str1[n]==\' \') {\n		n++; \n	}\n	str1+=n;\n	length=(int)strlen(str1); \n	if(length &gt; 0){\n		for (i=length-1;i&gt;0;i--) {\n			if (str1[i] == \' \')\n				str1[i] = \'\\0\';\n			else\n				break;\n		}\n	} else {\n		str1[0] = \'\\0\';\n	}\n	strcpy(tempstr,str1);\n	return tempstr;\n}<e>[/code]</e></CODE></r>', '410e462892137f1d1e8190275238a8ac', 0, 'AIA=', '1eh46f85', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(22, 21, 5, 53, 0, '10.48.12.129', 1242905866, 0, 1, 1, 1, 1, '', 'Load Market Data', '<r>Sample Code\n<CODE><s>[code]</s>sMKTPOINTLIST MktPointList;\n	sMKTPOINT * MktPoint;\n	// Load Market Data\n	LoadMktData(&amp;MktPointList,CurveID,OutCcy,fxIndex1,sAsOfDate());\n\n	for(int j = 0 ; j&lt; MktPointList.List.ItemsUsed;j++){\n		if ( !(MktPoint = (sMKTPOINT*) sGetListItem( (void*)&amp;MktPointList, j ))) {\n			sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , j);\n			return sERROR;\n		}\n\n		printf(\"Date = %s,Rate = %f\\n\",sGCDate(Buf, MktPoint-&gt;Date),MktPoint-&gt;MidRate);\n	}<e>[/code]</e></CODE>\nFunction\n<CODE><s>[code]</s>int LoadMktData(sMKTPOINTLIST * MktPointListRt,sCURVE_ID CurveID,sCCY Ccy,sINDEX Index,sIDATE AsOfDate){\n\n	int err;\n\n	sENTITY * MktDataEnt;\n	sMKTDATA * Mktdata;\n\n	sSetCurveID(CurveID.ID);\n	// Init MktPointListRT\n	mSetList(MktPointListRt-&gt;List, sizeof( sMKTPOINT ), offsetof( sMKTPOINTLIST, Data ));\n	if ( sInitList( &amp;MktPointListRt-&gt;List, \"PRESET\" ) ) {\n		delete MktPointListRt;\n		MktPointListRt = NULL;\n		sLogMessage(\"Error Initializing List\", sSERIOUS, 0);\n		exit(sERROR);\n	}\n\n	MktDataEnt = sGetEntityByName( \"MKTDATA\" );\n	if ( sEntityCreate( MktDataEnt, (void **)&amp;Mktdata ))\n	{\n		mLogMessage( \" Error allocating customer structure\" );\n		exit( sERROR );\n	}\n\n	sEntityInit(MktDataEnt, (void *) Mktdata, sINIT );\n\n	// Assign Key Word\n	strcpy(Mktdata-&gt;Id.ID,CurveID.ID);\n	strcpy(Mktdata-&gt;Ccy.Name,Ccy.Name);\n	strcpy(Mktdata-&gt;Index.Name,Index.Name);\n	Mktdata-&gt;AsOfDate = AsOfDate;\n\n	err = sEntityDBRead( MktDataEnt, (void *)Mktdata, \"EQUAL\", sNO_ZERO_REC );\n	if ( err &amp;&amp; ( err != sDB_FAIL ))\n	{\n		mLogMessage( \"Abnormal read termination, cancel read.\" );\n		sEntityDBRead( MktDataEnt, Mktdata, \"CANCEL\", 00 );\n		sEntityFree( MktDataEnt, (void **)&amp;Mktdata, sYES ); \n		return sERROR;\n	}\n	sEntityDBRead( MktDataEnt, Mktdata, \"CANCEL\", 00 );\n\n	// Load From MktSpec\n	sMKTSPECLIST * MktSpecList;\n	sMKTSPEC * MktSpec;\n	MktSpecList = &amp;Mktdata-&gt;MktSpecs;\n\n	sMKTPOINTLIST * MktPointList;\n	sMKTPOINT * MktPoint , NewMktPoint;\n\n	sEntityInit(sGetEntityByName(\"MKTPOINT\"), (void *) &amp;NewMktPoint, sINIT );\n\n	for(int i = 0 ; i&lt; MktSpecList-&gt;List.ItemsUsed;i++){\n		if ( !(MktSpec = (sMKTSPEC*) sGetListItem( (void*)MktSpecList, i ))) {\n			sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , i);\n			return sERROR;\n		}\n		MktPointList = &amp;MktSpec-&gt;Rates;\n\n		for(int j = 0 ; j&lt; MktPointList-&gt;List.ItemsUsed;j++){\n			if ( !(MktPoint = (sMKTPOINT*) sGetListItem( (void*)MktPointList, j ))) {\n				sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , j);\n				return sERROR;\n			}\n			memcpy((void *)&amp;NewMktPoint,(void *)MktPoint,sizeof(sMKTPOINT));\n			sInsertListItem( (void *)MktPointListRt, MktPointListRt-&gt;List.ItemsUsed, (void *)&amp;NewMktPoint );\n		}\n	}\n	return sSUCCESS;\n}<e>[/code]</e></CODE></r>', 'f32df44b1453a419cab9e044381e2eca', 0, 'AIA=', '2fxhmxnu', 1, 0, '', 0, 0, 0, 1, 0, '', 0);
