(23, 22, 5, 53, 0, '10.48.12.129', 1242906219, 0, 1, 1, 1, 1, '', 'Get CommSet Data', '<r>Sample1\n<CODE><s>[code]</s>// Get Commset\n	sCOMMDATASET * CommSet;\n	sEntityCreate( sGetEntityByName(\"COMMSET\"), (void **)&amp;CommSet);\n	strcpy(CommSet-&gt;Id.ID,\"EOD\");\n	CommSet-&gt;Type = sCT_BOND_CLOSING;\n	CommSet-&gt;AsOfDate = sSybIDate(\"20081009\");\n	//sprintf(CommSet-&gt;Name.Name,\"%s/%s/%s\",Trim(sICEnum(\"sCOMMTYPE\",CommSet-&gt;Type,sSHORT_ENUM)),quoted-&gt;Ccy1.Name,quoted-&gt;Ccy2.Name);\n	//strcpy(CommSet-&gt;Name.Name,\"FXRATE/EUR/USD\");\n	sprintf(CommSet-&gt;Name.Name,\"%s~%s~%s~%s\",\"BOND\",\"TWD\",\"BTP\",\"15221\");\n\n	err = sEntityDBRead( sGetEntityByName(\"COMMSET\"), (void *)CommSet, \"EQUAL\", sNO_ZERO_REC );\n\n	if ( err &amp;&amp; ( err != sDB_FAIL ))\n	{\n		mLogMessage( \"Abnormal read termination, cancel read.\" );\n		sEntityDBRead( sGetEntityByName(\"COMMSET\"), CommSet, \"CANCEL\", 00 );\n		sEntityFree( sGetEntityByName(\"COMMSET\"), (void **)&amp;CommSet, sYES ); \n		return sERROR;\n	}\n	sEntityDBRead( sGetEntityByName(\"COMMSET\"), CommSet, \"CANCEL\", 00 );\n\n	if((err = sGetFXCommData(CommSet,NULL,00)) == sERROR){\n		mLogMessage(\"Error Get\");\n	}<e>[/code]</e></CODE>\nSample 2\n<CODE><s>[code]</s><i>\n</i>	sIRATE Rate;\n	sIDATE ReqDate;\n	sCCY OutCcy,InCcy;\n	sCVLLIST CvlList;\n	sUINT Mode = 0;\n	//Mode |= 040;\n	Mode |= 0100;\n	char Buf[100];\n	sCURVE_ID CurveID;\n	strcpy(CurveID.ID,\"EOD\");\n\nReqDate = sAsOfDate();\nf(sInitCvLList( &amp;CvlList, sINIT )){\n		mLogMessage(\"Error Init Curve List\");\n		return sERROR;\n	}\n	sSetCurveID(\"EOD\");\n	strcpy(OutCcy.Name,\"TWD\");\n	strcpy(InCcy.Name,\"USD\");\n	err = sGetFXRate( &amp;Rate, ReqDate, OutCcy, InCcy,\n		sAsOfDate(),  &amp;CvlList , Mode, sNO_BA );\n	if(err){\n		mLogMessage(\"Error Get Rate\");\n		return sERROR;\n	}\n	sQUOTED * quoted = sQuotedCurrency( &amp;InCcy, &amp;OutCcy, NULL, 0 );\n\n// Get Commset\n	sCOMMDATASET * CommSet;\n	sEntityCreate( sGetEntityByName(\"COMMSET\"), (void **)&amp;CommSet);\n	strcpy(CommSet-&gt;Id.ID,\"EOD\");\n	CommSet-&gt;Type = sCT_FX_RATE;\n	CommSet-&gt;AsOfDate = sAsOfDate();\n	sprintf(CommSet-&gt;Name.Name,\"FXRATE/%s/%s\",quoted-&gt;Ccy1.Name,quoted-&gt;Ccy2.Name);\n	//strcpy(CommSet-&gt;Name.Name,\"FXRATE/EUR/USD\");\n\n	if((err = sGetFXCommData(CommSet,NULL,00)) == sERROR){\n		mLogMessage(\"Error Get\");\n	}\n\n	sCOMMDATA * CommData;\n	for(int i = 0 ; i&lt; CommSet-&gt;DataList.List.ItemsUsed;i++){\n		if ( !(CommData = (sCOMMDATA*) sGetListItem( (void*)&amp;CommSet-&gt;DataList, i ))) {\n			sLogMessage( \"Error getting trade %d from list\", sSERIOUS, 0 , i);\n			return sERROR;\n		}\n		printf(\"Date = %s,MidRate = %f,BidRate = %f, AskRate = %f\\n\",sGCDate(Buf, CommData-&gt;Date),CommData-&gt;Value,CommData-&gt;BidValue,CommData-&gt;AskValue);\n	}<e>[/code]</e></CODE></r>', '3c8d66eca1c85bd52a627341c7700343', 0, 'AIA=', '39tr8pv2', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
