(32, 31, 6, 53, 0, '10.48.12.129', 1243010137, 0, 1, 1, 1, 1, '', 'Capture Screen', '<r>Sample Code:\n<CODE><s>[code]</s>  Bitmap BP = new Bitmap( CaptureScreen.GetDesktopImage());\n            BP.Save(@\"C:\\temp\\test.png\",ImageFormat.Png);<e>[/code]</e></CODE>\n\nCaptureScreen.cs\n<ATTACHMENT filename=\"CaptureScreen.cs\" index=\"2\"><s>[attachment=2]</s>CaptureScreen.cs<e>[/attachment]</e></ATTACHMENT>\n<CODE><s>[code]</s>using System;\nusing System.Drawing;\n\n\nnamespace CaptureScreen\n{\n	/// &lt;summary&gt;\n	/// This class shall keep all the functionality for capturing \n	/// the desktop.\n	/// &lt;/summary&gt;\n	public class CaptureScreen\n	{\n		#region Public Class Functions\n		public static Bitmap GetDesktopImage()\n		{\n			//In size variable we shall keep the size of the screen.\n			SIZE size;\n			\n			//Variable to keep the handle to bitmap.\n			IntPtr hBitmap;\n\n			//Here we get the handle to the desktop device context.\n			IntPtr 	hDC = PlatformInvokeUSER32.GetDC(PlatformInvokeUSER32.GetDesktopWindow()); \n\n			//Here we make a compatible device context in memory for screen device context.\n			IntPtr hMemDC = PlatformInvokeGDI32.CreateCompatibleDC(hDC);\n			\n			//We pass SM_CXSCREEN constant to GetSystemMetrics to get the X coordinates of screen.\n			size.cx = PlatformInvokeUSER32.GetSystemMetrics(PlatformInvokeUSER32.SM_CXSCREEN);\n\n			//We pass SM_CYSCREEN constant to GetSystemMetrics to get the Y coordinates of screen.\n			size.cy = PlatformInvokeUSER32.GetSystemMetrics(PlatformInvokeUSER32.SM_CYSCREEN);\n			\n			//We create a compatible bitmap of screen size using screen device context.\n			hBitmap = PlatformInvokeGDI32.CreateCompatibleBitmap(hDC, size.cx, size.cy);\n\n			//As hBitmap is IntPtr we can not check it against null. For this purspose IntPtr.Zero is used.\n			if (hBitmap!=IntPtr.Zero)\n			{\n				//Here we select the compatible bitmap in memeory device context and keeps the refrence to Old bitmap.\n				IntPtr hOld = (IntPtr) PlatformInvokeGDI32.SelectObject(hMemDC, hBitmap);\n				//We copy the Bitmap to the memory device context.\n				PlatformInvokeGDI32.BitBlt(hMemDC, 0, 0,size.cx,size.cy, hDC, 0, 0, PlatformInvokeGDI32.SRCCOPY);\n				//We select the old bitmap back to the memory device context.\n				PlatformInvokeGDI32.SelectObject(hMemDC, hOld);\n				//We delete the memory device context.\n				PlatformInvokeGDI32.DeleteDC(hMemDC);\n				//We release the screen device context.\n				PlatformInvokeUSER32.ReleaseDC(PlatformInvokeUSER32.GetDesktopWindow(), hDC);\n				//Image is created by Image bitmap handle and stored in local variable.\n				Bitmap bmp = System.Drawing.Image.FromHbitmap(hBitmap); \n				//Release the memory for compatible bitmap.\n				PlatformInvokeGDI32.DeleteObject(hBitmap);\n				//This statement runs the garbage collector manually.\n				GC.Collect();\n				//Return the bitmap \n				return bmp;\n			}\n		\n			//If hBitmap is null retunrn null.\n			return null;\n		}\n		#endregion\n	}\n}\n<e>[/code]</e></CODE>\nPlatFormInvokeGDI32.cs\n<ATTACHMENT filename=\"PlatFormInvokeGDI32.cs\" index=\"1\"><s>[attachment=1]</s>PlatFormInvokeGDI32.cs<e>[/attachment]</e></ATTACHMENT>\n<CODE><s>[code]</s>using System;\nusing System.Runtime.InteropServices;\nnamespace CaptureScreen\n{\n	/// &lt;summary&gt;\n	/// This class shall keep the GDI32 APIs being used in our program.\n	/// &lt;/summary&gt;\n	public class PlatformInvokeGDI32\n	{\n\n		#region Class Variables\n		public  const int SRCCOPY = 13369376;\n		#endregion\n\n		#region Class Functions\n		[DllImport(\"gdi32.dll\",EntryPoint=\"DeleteDC\")]\n		public static extern IntPtr DeleteDC(IntPtr hDc);\n\n		[DllImport(\"gdi32.dll\",EntryPoint=\"DeleteObject\")]\n		public static extern IntPtr DeleteObject(IntPtr hDc);\n\n		[DllImport(\"gdi32.dll\",EntryPoint=\"BitBlt\")]\n		public static extern bool BitBlt(IntPtr hdcDest,int xDest,int yDest,int wDest,int hDest,IntPtr hdcSource,int xSrc,int ySrc,int RasterOp);\n\n		[DllImport (\"gdi32.dll\",EntryPoint=\"CreateCompatibleBitmap\")]\n		public static extern IntPtr CreateCompatibleBitmap(IntPtr hdc,	int nWidth, int nHeight);\n\n		[DllImport (\"gdi32.dll\",EntryPoint=\"CreateCompatibleDC\")]\n		public static extern IntPtr CreateCompatibleDC(IntPtr hdc);\n\n		[DllImport (\"gdi32.dll\",EntryPoint=\"SelectObject\")]\n		public static extern IntPtr SelectObject(IntPtr hdc,IntPtr bmp);\n		#endregion\n	\n		#region Public Constructor\n		public PlatformInvokeGDI32()\n		{\n			// \n			// TODO: Add constructor logic here\n			//\n		}\n		#endregion\n	\n	}\n}\n<e>[/code]</e></CODE>\n\nPlatformInvokeUSER32.cs\n<ATTACHMENT filename=\"PlatformInvokeUSER32.cs\" index=\"0\"><s>[attachment=0]</s>PlatformInvokeUSER32.cs<e>[/attachment]</e></ATTACHMENT>\n<CODE><s>[code]</s>using System;\nusing System.Runtime.InteropServices;\nnamespace CaptureScreen\n{\n	/// &lt;summary&gt;\n	/// This class shall keep the User32 APIs being used in \n	/// our program.\n	/// &lt;/summary&gt;\n	public class PlatformInvokeUSER32\n	{\n\n		#region Class Variables\n		public  const int SM_CXSCREEN=0;\n		public  const int SM_CYSCREEN=1;\n		#endregion		\n		\n		#region Class Functions\n		[DllImport(\"user32.dll\", EntryPoint=\"GetDesktopWindow\")]\n		public static extern IntPtr GetDesktopWindow();\n\n		[DllImport(\"user32.dll\",EntryPoint=\"GetDC\")]\n		public static extern IntPtr GetDC(IntPtr ptr);\n\n		[DllImport(\"user32.dll\",EntryPoint=\"GetSystemMetrics\")]\n		public static extern int GetSystemMetrics(int abc);\n\n		[DllImport(\"user32.dll\",EntryPoint=\"GetWindowDC\")]\n		public static extern IntPtr GetWindowDC(Int32 ptr);\n\n		[DllImport(\"user32.dll\",EntryPoint=\"ReleaseDC\")]\n		public static extern IntPtr ReleaseDC(IntPtr hWnd,IntPtr hDc);\n		\n		#endregion\n\n		#region Public Constructor\n		public PlatformInvokeUSER32()\n		{\n			// \n			// TODO: Add constructor logic here\n			//\n		}\n		#endregion\n	}\n\n	//This structure shall be used to keep the size of the screen.\n	public struct SIZE\n	{\n		public int cx;\n		public int cy;\n	}\n}\n<e>[/code]</e></CODE></r>', '500e1c2851a37bc260ec9bec74207fbe', 1, 'AIg=', '2a0l062u', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(33, 32, 6, 53, 0, '10.48.12.129', 1243010599, 0, 1, 1, 1, 1, '', '鍵盤攔截程式', '<r>Sample Code:\n<ATTACHMENT filename=\"KeyBoardHookTest.cs\" index=\"0\"><s>[attachment=0]</s>KeyBoardHookTest.cs<e>[/attachment]</e></ATTACHMENT>\n<CODE><s>[code]</s>using System;\nusing System.Collections.Generic;\nusing System.ComponentModel;\nusing System.Data;\nusing System.Drawing;\nusing System.Linq;\nusing System.Text;\nusing System.Windows.Forms;\n\n\nnamespace KeyBoardHookTest\n{\n\n    public partial class NumberPanel : Form\n    {\n        const int WH_KEYBOARD = 2;\n\n        public delegate int HookProc(int nCode, IntPtr wParam, IntPtr lParam);\n\n        private static int m_HookHandle = 0;    // Hook handle\n        private HookProc m_KbdHookProc;            // 鍵盤掛鉤函式指標\n\n        // 設置掛鉤.\n        [DllImport(\"user32.dll\", CharSet = CharSet.Auto,\n        CallingConvention = CallingConvention.StdCall)]\n        public static extern int SetWindowsHookEx(int idHook, HookProc lpfn,\n        IntPtr hInstance, int threadId);\n\n        // 將之前設置的掛鉤移除。記得在應用程式結束前呼叫此函式.\n        [DllImport(\"user32.dll\", CharSet = CharSet.Auto,\n        CallingConvention = CallingConvention.StdCall)]\n        public static extern bool UnhookWindowsHookEx(int idHook);\n\n        // 呼叫下一個掛鉤處理常式（若不這麼做，會令其他掛鉤處理常式失效）.\n        [DllImport(\"user32.dll\", CharSet = CharSet.Auto,\n        CallingConvention = CallingConvention.StdCall)]\n        public static extern int CallNextHookEx(int idHook, int nCode,\n        IntPtr wParam, IntPtr lParam);\n\n        [DllImport(\"kernel32.dll\")]\n        static extern int GetCurrentThreadId();\n\n        public static int KeyboardHookProc(int nCode, IntPtr wParam, IntPtr lParam)\n        {\n            // 當按鍵按下及鬆開時都會觸發此函式，這裡只處理鍵盤按下的情形。\n            bool isPressed = (lParam.ToInt32() &amp; 0x80000000) == 0;\n\n            if (nCode &lt; 0 || !isPressed)\n            {\n                return CallNextHookEx(m_HookHandle, nCode, wParam, lParam);\n            }\n\n            // 取得欲攔截之按鍵狀態\n            KeyStateInfo ctrlKey = KeyboardInfo.GetKeyState(Keys.ControlKey);\n            KeyStateInfo altKey = KeyboardInfo.GetKeyState(Keys.Alt);\n            KeyStateInfo shiftKey = KeyboardInfo.GetKeyState(Keys.ShiftKey);\n            KeyStateInfo f8Key = KeyboardInfo.GetKeyState(Keys.F8);\n\n            if (ctrlKey.IsPressed)\n            {\n                System.Diagnostics.Debug.WriteLine(\"Ctrl Pressed!\");\n            }\n            if (altKey.IsPressed)\n            {\n                System.Diagnostics.Debug.WriteLine(\"Alt Pressed!\");\n            }\n            if (shiftKey.IsPressed)\n            {\n                System.Diagnostics.Debug.WriteLine(\"Shift Pressed!\");\n            }\n            if (f8Key.IsPressed)\n            {\n                System.Diagnostics.Debug.WriteLine(\"F8 Pressed!\");\n            }\n\n            return CallNextHookEx(m_HookHandle, nCode, wParam, lParam);\n        }\n\n        public NumberPanel()\n        {\n            InitializeComponent();\n        }\n\n        private void NumberPanel_Load(object sender, EventArgs e)\n        {\n\n        }\n\n        private void SetHook()\n        {\n            if (m_HookHandle == 0)\n            {\n                m_KbdHookProc = new HookProc(NumberPanel.KeyboardHookProc);\n\n                m_HookHandle = SetWindowsHookEx(WH_KEYBOARD, m_KbdHookProc, IntPtr.Zero, GetCurrentThreadId());\n\n                if (m_HookHandle == 0)\n                {\n                    MessageBox.Show(\"呼叫 SetWindowsHookEx 失敗!\");\n                    return;\n                }\n               \n            }\n            else\n            {\n                bool ret = UnhookWindowsHookEx(m_HookHandle);\n                if (ret == false)\n                {\n                    MessageBox.Show(\"呼叫 UnhookWindowsHookEx 失敗!\");\n                    return;\n                }\n                m_HookHandle = 0;\n\n            }\n        }\n\n    }\n    public class KeyboardInfo\n    {\n        private KeyboardInfo() { }\n\n        [DllImport(\"user32\")]\n        private static extern short GetKeyState(int vKey);\n\n        public static KeyStateInfo GetKeyState(Keys key)\n        {\n            int vkey = (int)key;\n\n            if (key == Keys.Alt)\n            {\n                vkey = 0x12;    // VK_ALT\n            }\n\n            short keyState = GetKeyState(vkey);\n            int low = Low(keyState);\n            int high = High(keyState);\n            bool toggled = (low == 1);\n            bool pressed = (high == 1);\n\n            return new KeyStateInfo(key, pressed, toggled);\n        }\n\n        private static int High(int keyState)\n        {\n            if (keyState &gt; 0)\n            {\n                return keyState &gt;&gt; 0x10;\n            }\n            else\n            {\n                return (keyState &gt;&gt; 0x10) &amp; 0x1;\n            }\n\n        }\n\n        private static int Low(int keyState)\n        {\n            return keyState &amp; 0xffff;\n        }\n    }\n\n\n    public struct KeyStateInfo\n    {\n        Keys m_Key;\n        bool m_IsPressed;\n        bool m_IsToggled;\n\n        public KeyStateInfo(Keys key, bool ispressed, bool istoggled)\n        {\n            m_Key = key;\n            m_IsPressed = ispressed;\n            m_IsToggled = istoggled;\n        }\n\n        public static KeyStateInfo Default\n        {\n            get\n            {\n                return new KeyStateInfo(Keys.None, false, false);\n            }\n        }\n\n        public Keys Key\n        {\n            get { return m_Key; }\n        }\n\n        public bool IsPressed\n        {\n            get { return m_IsPressed; }\n        }\n\n        public bool IsToggled\n        {\n            get { return m_IsToggled; }\n        }\n    }\n}\n<e>[/code]</e></CODE>\nKeyboardHook.cs\n<ATTACHMENT filename=\"KeyboardHook.cs\" index=\"1\"><s>[attachment=1]</s>KeyboardHook.cs<e>[/attachment]</e></ATTACHMENT>\n<CODE><s>[code]</s>   using System;\n   using System.ComponentModel;\n   using System.Windows.Forms;\n   using System.Diagnostics;\n   using System.Runtime.InteropServices;\n   \n   namespace KeyboardHook\n   {\n       public partial class Form1 : Form\n       {\n           public Form1()\n           {\n               InitializeComponent();\n           }\n   \n           const int WH_KEYBOARD = 2;\n   \n           public delegate int HookProc(int nCode, IntPtr wParam, IntPtr lParam);\n   \n           private static int m_HookHandle = 0;    // Hook handle\n           private HookProc m_KbdHookProc;            // 鍵盤掛鉤函式指標\n   \n           // 設置掛鉤.\n           [DllImport(\"user32.dll\", CharSet = CharSet.Auto,\n           CallingConvention = CallingConvention.StdCall)]\n           public static extern int SetWindowsHookEx(int idHook, HookProc lpfn,\n           IntPtr hInstance, int threadId);\n   \n           // 將之前設置的掛鉤移除。記得在應用程式結束前呼叫此函式.\n           [DllImport(\"user32.dll\", CharSet = CharSet.Auto,\n           CallingConvention = CallingConvention.StdCall)]\n           public static extern bool UnhookWindowsHookEx(int idHook);\n   \n           // 呼叫下一個掛鉤處理常式（若不這麼做，會令其他掛鉤處理常式失效）.\n           [DllImport(\"user32.dll\", CharSet = CharSet.Auto,\n           CallingConvention = CallingConvention.StdCall)]\n           public static extern int CallNextHookEx(int idHook, int nCode,\n           IntPtr wParam, IntPtr lParam);\n   \n           [DllImport(\"kernel32.dll\")]\n           static extern int GetCurrentThreadId();\n   \n           private void button1_Click(object sender, EventArgs e)\n           {\n               if (m_HookHandle == 0)\n               {\n                   m_KbdHookProc = new HookProc(Form1.KeyboardHookProc);\n   \n                   m_HookHandle = SetWindowsHookEx(WH_KEYBOARD, m_KbdHookProc, IntPtr.Zero, GetCurrentThreadId());\n   \n                   if (m_HookHandle == 0)\n                   {\n                       MessageBox.Show(\"呼叫 SetWindowsHookEx 失敗!\");\n                       return;\n                   }\n                   button1.Text = \"解除鍵盤掛鉤\";\n               }\n               else\n               {\n                   bool ret = UnhookWindowsHookEx(m_HookHandle);\n                   if (ret == false)\n                   {\n                       MessageBox.Show(\"呼叫 UnhookWindowsHookEx 失敗!\");\n                       return;\n                   }\n                   m_HookHandle = 0;\n                   button1.Text = \"設置鍵盤掛鉤\";\n               }\n           }\n   \n           public static int KeyboardHookProc(int nCode, IntPtr wParam, IntPtr lParam)\n           {\n               // 當按鍵按下及鬆開時都會觸發此函式，這裡只處理鍵盤按下的情形。\n               bool isPressed = (lParam.ToInt32() &amp; 0x80000000) == 0;   \n   \n               if (nCode &lt; 0 || !isPressed)\n               {\n                   return CallNextHookEx(m_HookHandle, nCode, wParam, lParam);\n               }\n   \n               // 取得欲攔截之按鍵狀態\n               KeyStateInfo ctrlKey = KeyboardInfo.GetKeyState(Keys.ControlKey);\n               KeyStateInfo altKey = KeyboardInfo.GetKeyState(Keys.Alt);\n               KeyStateInfo shiftKey = KeyboardInfo.GetKeyState(Keys.ShiftKey);\n               KeyStateInfo f8Key = KeyboardInfo.GetKeyState(Keys.F8);\n   \n               if (ctrlKey.IsPressed)\n               {\n                   System.Diagnostics.Debug.WriteLine(\"Ctrl Pressed!\");\n               }\n               if (altKey.IsPressed)\n               {\n                   System.Diagnostics.Debug.WriteLine(\"Alt Pressed!\");\n               }\n               if (shiftKey.IsPressed)\n               {\n                   System.Diagnostics.Debug.WriteLine(\"Shift Pressed!\");\n               }\n               if (f8Key.IsPressed)\n               {\n                   System.Diagnostics.Debug.WriteLine(\"F8 Pressed!\");\n               }\n   \n               return CallNextHookEx(m_HookHandle, nCode, wParam, lParam);\n           }\n       }\n   \n       public class KeyboardInfo\n       {\n           private KeyboardInfo() { }\n   \n           [DllImport(\"user32\")]\n           private static extern short GetKeyState(int vKey);\n   \n           public static KeyStateInfo GetKeyState(Keys key)\n           {\n               int vkey = (int)key;\n   \n               if (key == Keys.Alt)\n               {\n                   vkey = 0x12;    // VK_ALT\n               }\n   \n               short keyState = GetKeyState(vkey);\n               int low = Low(keyState);\n               int high = High(keyState);\n               bool toggled = (low == 1);\n               bool pressed = (high == 1);\n   \n               return new KeyStateInfo(key, pressed, toggled);\n           }\n   \n           private static int High(int keyState)\n           {\n               if (keyState &gt; 0)\n               {\n                   return keyState &gt;&gt; 0x10;\n               }\n               else\n               {\n                   return (keyState &gt;&gt; 0x10) &amp; 0x1;\n               }\n   \n           }\n   \n           private static int Low(int keyState)\n           {\n               return keyState &amp; 0xffff;\n           }\n       }\n   \n   \n       public struct KeyStateInfo\n       {\n           Keys m_Key;\n           bool m_IsPressed;\n           bool m_IsToggled;\n   \n           public KeyStateInfo(Keys key, bool ispressed, bool istoggled)\n           {\n               m_Key = key;\n               m_IsPressed = ispressed;\n               m_IsToggled = istoggled;\n           }\n   \n           public static KeyStateInfo Default\n           {\n               get\n               {\n                   return new KeyStateInfo(Keys.None, false, false);\n               }\n           }\n   \n           public Keys Key\n           {\n               get { return m_Key; }\n           }\n   \n           public bool IsPressed\n           {\n               get { return m_IsPressed; }\n           }\n   \n           public bool IsToggled\n           {\n               get { return m_IsToggled; }\n           }\n       }\n   }<e>[/code]</e></CODE></r>', '0a259e5eaaad155f842c78139938bd7a', 1, 'AIg=', '2kyg2udp', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
