(138, 128, 6, 53, 0, '10.48.2.73', 1310529195, 0, 1, 1, 1, 1, '', 'C#常用補0補空白的方法', '<t>//不滿特定長度的字串，後面補空白 <br/>\nConsole.WriteLine(String.Format(\"-&gt;{0,-10}&lt;-\", \"Hello\")); //-&gt;Hello     &lt;-<br/>\n<br/>\n//不滿特定長度的字串，前面補空白 <br/>\nConsole.WriteLine(String.Format(\"-&gt;{0,10}&lt;-\", \"Hello\")); //-&gt;     Hello&lt;-<br/>\n<br/>\n//前面補0的數字字串 <br/>\nConsole.WriteLine(String.Format(\"{0:0000}\", 157)); // 輸出 0157<br/>\n<br/>\n//前後都補0的數字字串 <br/>\nConsole.WriteLine(String.Format(\"{0:0000.0000}\", 157.42)); // 輸出 0157.4200<br/>\n<br/>\n//金額的表示, 每3位數(千)加逗號 <br/>\nConsole.WriteLine(String.Format(\"{0:0,0}\", 41038560)); // 輸出 41,038,560 <br/>\nConsole.WriteLine(String.Format(\"{0:0,0}\", 0)); // 輸出 00, 不好<br/>\n<br/>\n//金額的表示 <br/>\nConsole.WriteLine(String.Format(\"{0:$#,##0.00;($#,##0.00);Zero}\", 0)); // 這個會顯示 Zero <br/>\nConsole.WriteLine(String.Format(\"{0:$#,##0.00;($#,##0.00);Zero}\", 1243.50)); // 這個會顯示 $1,243.50<br/>\n<br/>\n//金額的表示_改良_取到小數2位 <br/>\nConsole.WriteLine(String.Format(\"{0:$###,###,###,##0.00}\", 0)); // $0.00 <br/>\nConsole.WriteLine(String.Format(\"{0:$###,###,###,##0.00}\", 12.5)); // $12.50 <br/>\nConsole.WriteLine(String.Format(\"{0:$###,###,###,##0.00}\", 3456234532)); // $3,456,234,532.0<br/>\n<br/>\n//金額的表示_改良2_取到個位 <br/>\nConsole.WriteLine(String.Format(\"{0:$#,0}\", 0)); // $0 <br/>\nConsole.WriteLine(String.Format(\"{0:$#,0}\", 12.5)); // $13,四拾五入到個位 <br/>\nConsole.WriteLine(String.Format(\"{0:$#,0}\", 3456234532)); // $3,456,234,532<br/>\n<br/>\n//格式化電話號碼 <br/>\nConsole.WriteLine(String.Format(\"{0:(###) ###-####}\", 8005551212)); // 輸出 (800) 555-1212<br/>\n<br/>\n//百分比 <br/>\nConsole.WriteLine(String.Format(\"{0:0%}\", 17 / (float)60)); // 輸出 28%<br/>\n<br/>\n//到小數2位的百分比 <br/>\nConsole.WriteLine(String.Format(\"{0:0.00%}\", 17 / (float)60)); // 輸出 28.33%<br/>\n<br/>\n//取小數點4位，並對第5位做四捨五入 <br/>\nConsole.WriteLine(String.Format(\"{0:#,0.####}\", 1234.56789)); // 1,234.5679<br/>\n<br/>\n//小數點不足4位不補0 <br/>\nConsole.WriteLine(String.Format(\"{0:0.####}\", 1234.567)); // 1234.567<br/>\n<br/>\n// 年/月/日 時:分:秒 毫秒<br/>\nConsole.WriteLine(DateTime.Now.ToString(\"yyyy/MM/dd HH:mm:ss ffff\"));</t>', '6e42aad311efb3251b97440f4e763faf', 0, '', '1sh7sel8', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
