(156, 144, 24, 53, 0, '10.48.12.128', 1326079796, 0, 1, 1, 1, 1, '', '增加CNH在OBU時，非USD的部位移轉到DBU', '<r>修改程式：stpClientModule2.cc,stpClientModule2.h,tradeserver_wfd.xml<br/>\n修改日期：2011/11/28<br/>\n<br/>\n後台人員提到非USD/CNH的部位不想留在OBU，但交易員不可能針對這種特殊狀況再輸入部位移轉的交易，因此IT協助客制化。<br/>\n修改概念：<br/>\n1.判斷交易是否有CNH的幣另<br/>\n2.再判斷另一邊是否非USD<br/>\n3.進行OBU部位抛除(即做一筆反向交易)<br/>\n4.進行DBU部位建立(即做一筆正向交易)<br/>\n<br/>\nstpClientModule2.cc\n<CODE><s>[code]</s><i>\n</i>SU_STP_BaseHook *pSplitCNHTrade = new SU_SplitCNHTrade(); // Add by Tomy on 2011/11/28\ngstpI-&gt;RegisterOperation(\"ClientModule2.SplitCNHTrade\", pSplitCNHTrade); // Add by Tomy on 2011/11/28\n\n// **************************************************************************\n// PROCEDURE: Execute\n//\n// PURPOSE:   1.Condition: FXFWD Trade Ccy is CNH\n//                       2.If not USD/CNH , We will move other side positon to DBU\n//                       3. And Envelop must be space\n// RETURNS:   sSUCCESS - Do success leg in XML Workflow\n//            sERROR   - Do exception leg in XML Workflow\n// CREATE :   2011/11/28 BY TomyLiu\n// **************************************************************************\nint SU_SplitCNHTrade::Execute(SU_EventModel *EventModel)\n{\n   char ErrorMsg[500];\n\n   char NewTradeId[20];\n   // Get the Entity\n   sENTITY *entity = (sENTITY*)EventModel-&gt;GetData(\"SU_ENTITY_STRUCTURE_POINTER_FROM_DS\");\n\n   if (!entity)\n   {\n      SU_GSTP_SkeletonI *gstpI = SU_GSTP_SkeletonProxy::CreateGSTPSkeletonProxy();\n      gstpI-&gt;PostException(sWF_EXCEPTION_WARN, SU_CString(\"Error getting entity pointer\"));\n      return sERROR;\n   }\n\n   // Check This is a Trade\n   if (!sIsTradeEntity(entity))\n      return sSUCCESS;\n\n   // Get The Trade Structure\n   sTRADE *pTrade = (sTRADE*)EventModel-&gt;GetData(\"SU_ENTITY_DATA_POINTER_FROM_DS\");\n\n   if (!pTrade)\n   {\n      SU_GSTP_SkeletonI *gstpI = SU_GSTP_SkeletonProxy::CreateGSTPSkeletonProxy();\n      gstpI-&gt;PostException(sWF_EXCEPTION_WARN, SU_CString(\"Error getting trade pointerL\"));\n      return sERROR;\n   }\n\n   sPORS PorS;\n   sCCY MainCcy, MoneyCcy;\n   sAMOUNT Amount, FarAmount;\n   sBOOK SpotBook, SwapBook;\n   sDESK_ID SpotDesk, SwapDesk;\n   sCUST_ID CustIdOBU, CustIdDBU;\n   sXRATE FwdPoint = 0.0, ShortFwdPoint = 0.0;\n   sAMOUNT OAmount = 0.0;\n   // Check Trade Type\n   switch (pTrade-&gt;Base.TradeType)\n   {\n      case sTT_FXFWD:\n         // Check Location is OBU\n         if (strcmp(pTrade-&gt;Base.Env-&gt;Company.Name, \"OBU\") == 0)\n         {\n            strcpy(SpotBook.Name, \"SP_3RD\");\n            strcpy(SpotDesk.Name, \"FX\");\n\n            if (strcmp(pTrade-&gt;FxTrade.Back-&gt;ExtSystem.Name, \"FXFWDNEGO\") == 0)\n            {\n               strcpy(SwapBook.Name, \"F_SW\");\n               strcpy(SwapDesk.Name, \"MONEY_MARKET\");\n               strcpy(CustIdOBU.Name, \"B0DBU\");\n               strcpy(CustIdDBU.Name, \"B0OBU\");\n            }\n            else\n            {\n               strcpy(SwapBook.Name, \"T_SW\");\n               strcpy(SwapDesk.Name, \"MONEY_MARKET\");\n               strcpy(CustIdOBU.Name, \"DBU\");\n               strcpy(CustIdDBU.Name, \"OBU\");\n            }\n\n            // Buy side is CNH\n            if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;BoughtCcy.Name, \"CNH\") == 0)\n            {\n               if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;SoldCcy.Name, \"USD\") != 0)\n               {\n                  // Check \n                  if (strcmp(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"\") == 0)\n                  {\n                     strcpy(MainCcy.Name, pTrade-&gt;FxTrade.Forex-&gt;SoldCcy.Name);\n                     strcpy(MoneyCcy.Name, \"USD\");\n                     Amount = pTrade-&gt;FxTrade.Forex-&gt;SoldAmt;\n\n                     PorS = sPURCHASE;\n\n\n                     if (!SimpleFXFWDTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MainCcy.Name, MoneyCcy.Name, Amount, CustIdOBU, SpotBook, SpotDesk, SwapBook, SwapDesk, \"OBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;FwdPoint, &amp;OAmount))\n                     {\n                        sprintf(ErrorMsg, \"Create OBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcpy(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n\n                     // SoldCcy is Main Ccy\n                     PorS = sSALE;\n                     if (!SimpleFXFWDTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MoneyCcy.Name, MainCcy.Name, Amount, CustIdDBU, SpotBook, SpotDesk, SwapBook, SwapDesk, \"DBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;FwdPoint, &amp;OAmount))\n                     {\n                        sprintf(ErrorMsg, \"Create DBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"+\");\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n                  }\n               }\n            }\n            if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;SoldCcy.Name, \"CNH\") == 0)\n            {\n               if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;BoughtCcy.Name, \"USD\") != 0)\n               {\n                  // Check \n                  if (strcmp(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"\") == 0)\n                  {\n\n                     strcpy(MainCcy.Name, pTrade-&gt;FxTrade.Forex-&gt;BoughtCcy.Name);\n                     strcpy(MoneyCcy.Name, \"USD\");\n                     Amount = pTrade-&gt;FxTrade.Forex-&gt;BoughtAmt;\n\n                     PorS = sSALE;\n                     if (!SimpleFXFWDTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MoneyCcy.Name, MainCcy.Name, Amount, CustIdOBU, SpotBook, SpotDesk, SwapBook, SwapDesk, \"OBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;FwdPoint, &amp;OAmount))\n                     {\n                        sprintf(ErrorMsg, \"Create OBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcpy(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n\n                     // SoldCcy is Main Ccy\n                     PorS = sPURCHASE;\n                     if (!SimpleFXFWDTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MainCcy.Name, MoneyCcy.Name, Amount, CustIdDBU, SpotBook, SpotDesk, SwapBook, SwapDesk, \"DBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;FwdPoint, &amp;OAmount))\n                     {\n                        sprintf(ErrorMsg, \"Create DBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"+\");\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n\n                  }\n               }\n            }\n         }\n         break;\n\n      case sTT_FXSPOT:\n         // Check Location is OBU\n         if (strcmp(pTrade-&gt;Base.Env-&gt;Company.Name, \"OBU\") == 0)\n         {\n            if ((strcmp(pTrade-&gt;Base.Env-&gt;Book.Name, \"TMUFX\") == 0) &amp;&amp; (strcmp(pTrade-&gt;Base.Env-&gt;Book.Name, \"TMU\") == 0))\n            {\n               strcpy(SpotBook.Name, \"TMUFX\");\n               strcpy(SpotDesk.Name, \"TMU\");\n            }\n            else\n            {\n               strcpy(SpotBook.Name, \"SP_3RD\");\n               strcpy(SpotDesk.Name, \"FX\");\n            }\n            strcpy(CustIdOBU.Name, \"DBU\");\n            strcpy(CustIdDBU.Name, \"OBU\");\n\n            // Buy side is CNH\n            if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;BoughtCcy.Name, \"CNH\") == 0)\n            {\n               if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;SoldCcy.Name, \"USD\") != 0)\n               {\n                  // Check \n                  if (strcmp(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"\") == 0)\n                  {\n                     strcpy(MainCcy.Name, pTrade-&gt;FxTrade.Forex-&gt;SoldCcy.Name);\n                     strcpy(MoneyCcy.Name, \"USD\");\n                     Amount = pTrade-&gt;FxTrade.Forex-&gt;SoldAmt;\n\n                     PorS = sPURCHASE;\n                     if (!SimpleFXSPOTTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MainCcy.Name, MoneyCcy.Name, Amount, CustIdOBU, SpotBook, SpotDesk, \"OBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;OAmount))\n                     {\n                        sprintf(ErrorMsg, \"Create OBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcpy(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n\n                     // SoldCcy is Main Ccy\n                     PorS = sSALE;\n                     if (!SimpleFXSPOTTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MoneyCcy.Name, MainCcy.Name, Amount, CustIdDBU, SpotBook, SpotDesk, \"DBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;OAmount))\n                     {\n                        sprintf(ErrorMsg, \"Create DBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"+\");\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n                  }\n               }\n            }\n            if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;SoldCcy.Name, \"CNH\") == 0)\n            {\n               if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;BoughtCcy.Name, \"USD\") != 0)\n               {\n                  // Check \n                  if (strcmp(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"\") == 0)\n                  {\n\n                     strcpy(MainCcy.Name, pTrade-&gt;FxTrade.Forex-&gt;BoughtCcy.Name);\n                     strcpy(MoneyCcy.Name, \"USD\");\n                     Amount = pTrade-&gt;FxTrade.Forex-&gt;BoughtAmt;\n\n                     PorS = sSALE;\n                     if (!SimpleFXSPOTTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MoneyCcy.Name, MainCcy.Name, Amount, CustIdOBU, SpotBook, SpotDesk, \"OBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;OAmount))\n                     {\n                        sprintf(ErrorMsg, \"Create OBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcpy(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n\n                     // SoldCcy is Main Ccy\n                     PorS = sPURCHASE;\n                     if (!SimpleFXSPOTTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MainCcy.Name, MoneyCcy.Name, Amount, CustIdDBU, SpotBook, SpotDesk, \"DBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;OAmount))\n                     {\n                        sprintf(ErrorMsg, \"Create DBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"+\");\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n\n                  }\n               }\n            }\n         }\n         break;\n      case sTT_FXSWAP:\n         // Check Location is OBU\n         if (strcmp(pTrade-&gt;Base.Env-&gt;Company.Name, \"OBU\") == 0)\n         {\n            strcpy(SpotBook.Name, \"SP_3RD\");\n            strcpy(SpotDesk.Name, \"FX\");\n            strcpy(SwapBook.Name, \"T_SW\");\n            strcpy(SwapDesk.Name, \"MONEY_MARKET\");\n            strcpy(CustIdOBU.Name, \"DBU\");\n            strcpy(CustIdDBU.Name, \"OBU\");\n\n\n            // Buy side is CNH\n            if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;BoughtCcy.Name, \"CNH\") == 0)\n            {\n               if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;SoldCcy.Name, \"USD\") != 0)\n               {\n                  // Check \n                  if (strcmp(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"\") == 0)\n                  {\n                     strcpy(MainCcy.Name, pTrade-&gt;FxTrade.Forex-&gt;SoldCcy.Name);\n                     strcpy(MoneyCcy.Name, \"USD\");\n                     Amount = pTrade-&gt;FxTrade.Forex-&gt;SoldAmt;\n                     FarAmount = pTrade-&gt;FxTrade.Forex-&gt;FwdBoughtAmt;\n\n                     PorS = sPURCHASE;\n                     if (!SimpleFXSWAPTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ShortValDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MainCcy.Name, MoneyCcy.Name, Amount, FarAmount, CustIdOBU, SpotBook, SpotDesk, SwapBook, SwapDesk, \"OBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;FwdPoint, &amp;ShortFwdPoint))\n                     {\n                        sprintf(ErrorMsg, \"Create OBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcpy(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n\n                     // SoldCcy is Main Ccy\n                     PorS = sSALE;\n                     if (!SimpleFXSWAPTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ShortValDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MoneyCcy.Name, MainCcy.Name, Amount, FarAmount, CustIdDBU, SpotBook, SpotDesk, SwapBook, SwapDesk, \"DBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;FwdPoint, &amp;ShortFwdPoint))\n                     {\n                        sprintf(ErrorMsg, \"Create DBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"+\");\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n                  }\n               }\n            }\n            if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;SoldCcy.Name, \"CNH\") == 0)\n            {\n               if (strcmp(pTrade-&gt;FxTrade.Forex-&gt;BoughtCcy.Name, \"USD\") != 0)\n               {\n                  // Check \n                  if (strcmp(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"\") == 0)\n                  {\n\n                     strcpy(MainCcy.Name, pTrade-&gt;FxTrade.Forex-&gt;BoughtCcy.Name);\n                     strcpy(MoneyCcy.Name, \"USD\");\n                     Amount = pTrade-&gt;FxTrade.Forex-&gt;BoughtAmt;\n                     FarAmount = pTrade-&gt;FxTrade.Forex-&gt;FwdSoldAmt;\n                     PorS = sSALE;\n                     if (!SimpleFXSWAPTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ShortValDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MoneyCcy.Name, MainCcy.Name, Amount, FarAmount, CustIdOBU, SpotBook, SpotDesk, SwapBook, SwapDesk, \"OBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;FwdPoint, &amp;ShortFwdPoint))\n                     {\n                        sprintf(ErrorMsg, \"Create OBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcpy(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n\n                     // SoldCcy is Main Ccy\n                     PorS = sPURCHASE;\n                     if (!SimpleFXSWAPTrade(pTrade-&gt;FxTrade.Env-&gt;TradeDate, pTrade-&gt;FxTrade.Forex-&gt;ShortValDate, pTrade-&gt;FxTrade.Forex-&gt;ValDate, PorS, MainCcy.Name, MoneyCcy.Name, Amount, FarAmount, CustIdDBU, SpotBook, SpotDesk, SwapBook, SwapDesk, \"DBU\", pTrade-&gt;Base.TradeID.Num, NewTradeId, &amp;FwdPoint, &amp;ShortFwdPoint))\n                     {\n                        sprintf(ErrorMsg, \"Create DBU Trade Error	,Source TradeId = %s\", pTrade-&gt;Base.TradeID.Num);\n                        mLogMessage(ErrorMsg);\n                        return sERROR;\n                     }\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, \"+\");\n                     strcat(pTrade-&gt;Base.Env-&gt;Comment2.Text, NewTradeId);\n\n                  }\n               }\n            }\n         }\n         break;\n   };\n   return sSUCCESS;\n}\n\nbool SimpleFXFWDTrade(sIDATE TradeDate, sIDATE ValDate, sPORS PorS, char *BCcy, char *SCcy, sAMOUNT Amount, sCUST_ID Cust, sBOOK SpotBook, sDESK_ID SpotDesk, sBOOK SwapBook, sDESK_ID SwapDesk, char *Location, char *LinkTradeId, char *NewTradeId, sXRATE *FwdPoint, sAMOUNT *OAmount)\n{\n\n   bool Flag = true;\n   char buf[500];\n\n   sENTITY *TradeEntity = sGetEntityByName(\"FXFWD\");\n   sFXTRADE *Trade = NULL;\n\n   if (sEntityCreate(TradeEntity, (void **) &amp;Trade))\n   {\n      mLogMessage(\"Error creating FxFWD Trade\");\n      sEntityFree(TradeEntity, (void **) &amp;Trade, sYES);\n   }\n   else\n   {\n      // Trade Date &amp; Trade Entry Date\n      Trade-&gt;Env-&gt;TradeDate = TradeDate;\n      Trade-&gt;Env-&gt;TradeEntryDate = TradeDate;\n      strcpy(Trade-&gt;Env-&gt;Company.Name, Location);\n      // Value Date\n      Trade-&gt;Forex-&gt;ValDate = ValDate;\n      // PorS\n      Trade-&gt;Forex-&gt;PorS = PorS;\n      // Bought Currency\n      strcpy(Trade-&gt;Forex-&gt;BoughtCcy.Name, BCcy);\n      // Sold Currency\n      strcpy(Trade-&gt;Forex-&gt;SoldCcy.Name, SCcy);\n      // Bought Amount\n      if (PorS == sPURCHASE)\n      {\n         Trade-&gt;Forex-&gt;BoughtAmt = Amount;\n         if (*OAmount != 0.0)\n            Trade-&gt;Forex-&gt;SoldAmt =  *OAmount;\n      }\n      if (PorS == sSALE)\n      {\n         Trade-&gt;Forex-&gt;SoldAmt = Amount;\n         if (*OAmount != 0.0)\n            Trade-&gt;Forex-&gt;BoughtAmt =  *OAmount;\n      }\n      // Auto Calcuate SpotDate\n      Trade-&gt;Forex-&gt;SpotDate = sFXSpotDate(Trade-&gt;Forex-&gt;BoughtCcy, Trade-&gt;Forex-&gt;SoldCcy, Trade-&gt;Env-&gt;TradeDate);\n\n      //Trade-&gt;Forex-&gt;SpotRate = atof(\"1.3515\");\n\n      // Put FwdPoint the same with OBU &amp; DBU\n      if (*FwdPoint != 0.0)\n         Trade-&gt;Forex-&gt;FwdPoints =  *FwdPoint;\n      //Trade-&gt;Forex-&gt;XRate = atof(TempStr);\n      //strcpy(Trade-&gt;Forex-&gt;SplitCcy.Name,TempStr);\n\n      //if Split Ccy &lt;&gt; \"\" means need split \n      if (!(strcmp(Trade-&gt;Forex-&gt;SplitCcy.Name, \"\") == 0))\n         Trade-&gt;Forex-&gt;SplitSpotOrSwap = sFXSPL_SPLIT_BOTH;\n\n      // SourceID\n      //strcpy(Trade-&gt;Back-&gt;ExtSystem.Name,TempStr);\n\n      // Customer\n      strcpy(Trade-&gt;Env-&gt;Cust.Name, Cust.Name);\n\n      // Defaulting the other data on the trade\n      if (sEntityFill(TradeEntity, Trade) == sERROR)\n      {\n         // Create Error Message\n         Flag = false;\n\n         sEntityFree(TradeEntity, (void **) &amp;Trade, sYES);\n      }\n\n      if (Flag)\n      {\n         sFX_BOOKDATA *BookData;\n\n         for (int j = 0; j &lt; Trade-&gt;Forex-&gt;BookData.List.ItemsUsed; j++)\n         {\n            if (BookData = (sFX_BOOKDATA*)sGetListItem((void*) &amp;Trade-&gt;Forex-&g