(204, 188, 6, 53, 0, '10.48.2.73', 1336123214, 0, 1, 1, 1, 1, '', '讓DataGridView可以使用Ctrl + V 貼東西', '<r><CODE><s>[code]</s> private void DGTradeData_KeyDown(object sender, KeyEventArgs e)\n        {\n            if ((e.Modifiers == Keys.Control) &amp;&amp; (e.KeyCode == Keys.V) &amp;&amp; (DGTradeData.CurrentCell != null))//Ctrl+V貼上指令\n            {\n                Match m = Regex.Match(Clipboard.GetText(), \"(.+)\\r?\\n?\");\n                if (m.Success)\n                {\n                    int i = 0, j = 0;\n                    while ((m.Groups[1].Value != \"\") &amp;&amp; \n                        (DGTradeData.RowCount - 1 &gt; DGTradeData.CurrentCell.RowIndex + i))\n                    {\n                        if (m.Groups[1].Value.Contains(\"\\t\"))\n                        {\n                            foreach (string column in m.Groups[1].Value.Replace(\"\\r\\n\", \"\").Split(\'\\t\'))\n                            {\n                                if ((DGTradeData.ColumnCount - 1) &gt; (DGTradeData.CurrentCell.ColumnIndex + j))\n                                {\n                                    DGTradeData[DGTradeData.CurrentCell.ColumnIndex + j, DGTradeData.CurrentCell.RowIndex + i].Value = column;\n                                    j++;\n                                }\n                            }\n                            j = 0;\n                        }\n                        else\n                            DGTradeData[DGTradeData.CurrentCell.ColumnIndex + j, DGTradeData.CurrentCell.RowIndex + i].Value = m.Groups[1].Value;\n                        i++;\n                        m = m.NextMatch();\n                    }\n                }\n                else\n                    DGTradeData[DGTradeData.CurrentCell.ColumnIndex, DGTradeData.CurrentCell.RowIndex].Value = Clipboard.GetText();\n            }\n        }<e>[/code]</e></CODE></r>', '01a9c2d06eee9bd25dac91a353feffde', 0, 'AIA=', '1y8npe96', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
