(303, 268, 6, 58, 0, '10.48.1.120', 1358324532, 0, 1, 1, 1, 1, '', 'c# &amp; 與&amp;&amp;運算子差異', '<r><CODE><s>[code]</s><i>\n</i>// cs_operator_logical_and.cs\nusing System;\nclass MainClass\n{\n   static bool Method1()\n   {\n      Console.WriteLine(\"Method1 called\");\n      return false;\n   }\n\n   static bool Method2()\n   {\n      Console.WriteLine(\"Method2 called\");\n      return true;\n   }\n\n   static void Main()\n   {\n      Console.WriteLine(\"regular AND:\");\n      Console.WriteLine(\"result is {0}\", Method1() &amp; Method2());\n      Console.WriteLine(\"short-circuit AND:\");\n      Console.WriteLine(\"result is {0}\", Method1() &amp;&amp; Method2());\n   }\n}\n<e>[/code]</e></CODE>\n\n<B><s>[b]</s>輸出<e>[/b]</e></B><br/>\nregular AND:<br/>\nMethod1 called<br/>\nMethod2 called<br/>\nresult is False<br/>\nshort-circuit AND:<br/>\nMethod1 called<br/>\nresult is False</r>', '52ee48246b19be54aa58874f314a34cb', 0, 'QIA=', '3fp86hgo', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
