(362, 321, 5, 58, 0, '10.48.1.120', 1389767991, 0, 1, 1, 1, 1, '', 'Summit 日期 格式轉換範例', '<r><CODE><s>[code]</s><i>\n</i>int TestDateCompute()\n{\n	sIDATE aDate;\n	sIDATE bDate;\n	sIDATE cDate;\n	sSDATE showDate;\n\n	//透過sIDATE函數轉換成sSDate格式日期\n	aDate = sAsOfDate();\n	showDate = sISDate(sAsOfDate());\n	sLogMessage(\"sAsOfDate() is %04d%02d%02d\", sSERIOUS, 0, showDate.Year, showDate.Month, showDate.Day);\n\n	//透過sCGDate函數計算+/-nD,+/-nW,+/-nM...等資料並轉換為sIDATE格式日期\n	bDate = sCGDate(\"-1D\");\n	//bDate = sCGDate(\"+1M\");\n	//bDate = sCGDate(\"-1Y\");\n	//再透過sGIDate函數計算加減後的sIDDATE日期資料\n	cDate = sGIDate(bDate, aDate);\n	showDate = sISDate(cDate);\n	sLogMessage(\"cDate is %04d%02d%02d\", sSERIOUS, 0, showDate.Year, showDate.Month, showDate.Day);\n\n	//將yyyymmdd日期資料透過sSybIDate函數轉換為sIDATE格式日期\n	char buf[9];\n	strcpy(buf, \"20131201\");\n	sIDATE tmpDate = sSybIDate(buf);\n	showDate = sISDate(tmpDate);\n	sLogMessage(\"showDate is %04d%02d%02d\", sSERIOUS, 0, showDate.Year, showDate.Month, showDate.Day);\n	\n	//將sIDATE日期資料透過sIYMDDate函數轉換為yyyymmdd格式日期\n	char buf1[9];\n	sIYMDDate(buf1, sAppDate());\n	sLogMessage(\"buf1 date is %08s\", sSERIOUS, 0, buf1);\n	\n	return sSUCCESS;\n}\n<e>[/code]</e></CODE></r>', '5b066429545586ad175209f77809bcaf', 0, 'AIA=', '16nd45jh', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(363, 322, 19, 59, 0, '10.48.2.140', 1392106754, 0, 1, 1, 1, 1, '', '快速批次啟動方式', '<r>Dears<br/>\n    若當天夜間批次因問題無法於正常時間啟動，評估後無法於上班時間前執行完畢<br/>\n    可以執行快速批次，之後再將相關的報表資料補齊<br/>\n<br/>\n    若要執行快速批次，請調整P:\\Batch\\sumprod53\\Main_Batch_GUI.cmd中的QuickBatch，將其設為TRUE<br/>\n        <COLOR color=\"#FF0000\"><s>[color=#FF0000]</s>set QuickBatch=TRUE<e>[/color]</e></COLOR>    <br/>\n<br/>\n    但是記得跑完後要將他調整回來，以免影響隔天的批次 <E>:D</E></r>', 'aabba97cf71abf9ded76b7afa94d42fa', 0, 'Ag==', 'mpilk951', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
