(365, 324, 48, 54, 0, '10.48.1.106', 1392172857, 0, 1, 1, 1, 1, '', 'upgrade簽呈', '<r><ATTACHMENT filename=\"upgrade簽呈.pdf\" index=\"0\"><s>[attachment=0]</s>upgrade簽呈.pdf<e>[/attachment]</e></ATTACHMENT>\n<ATTACHMENT filename=\"附件1.1(硬體).xls\" index=\"1\"><s>[attachment=1]</s>附件1.1(硬體).xls<e>[/attachment]</e></ATTACHMENT>\n<ATTACHMENT filename=\"附件1.2(硬體).pdf\" index=\"2\"><s>[attachment=2]</s>附件1.2(硬體).pdf<e>[/attachment]</e></ATTACHMENT>\n<ATTACHMENT filename=\"附件1.3(硬體).xls\" index=\"3\"><s>[attachment=3]</s>附件1.3(硬體).xls<e>[/attachment]</e></ATTACHMENT>\n<ATTACHMENT filename=\"附件2_資通報價.xls\" index=\"4\"><s>[attachment=4]</s>附件2_資通報價.xls<e>[/attachment]</e></ATTACHMENT>\n<ATTACHMENT filename=\"附件3 Mysis-Summit維護合約.tif\" index=\"5\"><s>[attachment=5]</s>附件3 Mysis-Summit維護合約.tif<e>[/attachment]</e></ATTACHMENT>\n<ATTACHMENT filename=\"附件4_2013Q4_ITSC.ppt\" index=\"6\"><s>[attachment=6]</s>附件4_2013Q4_ITSC.ppt<e>[/attachment]</e></ATTACHMENT></r>', 'b0bf546ca31416437d50dfb8fc9d6ee5', 1, 'AAg=', '3t6c4mqo', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
