(460, 402, 48, 53, 0, '10.48.2.73', 1438237757, 0, 1, 1, 1, 1, '', '統計從2015-03-01之後，每筆交易放行時間', '<r>請放置於Summit DB內執行<br/>\n\n<CODE><s>[code]</s>select a.audit_version,a.audit_id,a.audit_datetime,a.audit_table,a.tradeid,b.peroid\nfrom dmenv_hist a left join (\n  select entityversion,entityid,min(period) peroid from (\n  SELECT entityversion,entityid, exceptionid,To_Date(substr(wm_concat(to_char(audit_datetime,\'YYYY-MM-DD HH24:MI:SS\')),0,19),\'YYYY-MM-DD HH24:MI:SS\') StartTime,To_Date(substr(wm_concat(to_char(audit_datetime,\'YYYY-MM-DD HH24:MI:SS\')),21,19),\'YYYY-MM-DD HH24:MI:SS\') EndTime, (To_Date(substr(wm_concat(to_char(audit_datetime,\'YYYY-MM-DD HH24:MI:SS\')),21,19),\'YYYY-MM-DD HH24:MI:SS\') - To_Date(substr(wm_concat(to_char(audit_datetime,\'YYYY-MM-DD HH24:MI:SS\')),0,19),\'YYYY-MM-DD HH24:MI:SS\')) *24*60*60  period \n  FROM (SELECT *  FROM dmWF_EXCEPTION where audit_action in (\'ACCEPT\',\'CLEAR\') order by Audit_action ) \n  group by entityversion,exceptionid,entityid) \n  where Period is not null\n  group by entityversion,entityid ) b on a.audit_id = b.entityid and a.audit_version = b.entityversion\n  where audit_entitystate = \'VER\' and b.peroid is not null\n  and to_char(audit_datetime,\'yyyy-mm-dd\') &gt; \'2015-03-01\'\n  order by a.audit_datetime<e>[/code]</e></CODE></r>', '1640e99dedfe3d6c5a968723ec48f25d', 0, 'AIA=', '197roe4l', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
