(501, 423, 49, 53, 0, '10.48.2.73', 1441954370, 0, 1, 1, 1, 1, '', '直接使用SQL Script Query DB並抓取資料', '<r>由cDBQuery呼叫使用\n<CODE><s>[code]</s>mSTATIC int cAllocGlobalDBQueryData( char * SqlCmd )\n{\n	gDBQueryRowCurrIndex = -1;\n\n	if( gDBQueryValList != NULL )\n	{\n		return sSUCCESS;\n	}\n\n	gDBQueryValList = new sVALLIST;\n	mSetList( gDBQueryValList-&gt;List, sizeof (sVALDATA), offsetof(sVALLIST,Data));\n\n	if ( sInitList(&amp;gDBQueryValList-&gt;List, \"PRESET\") != sSUCCESS )\n	{\n		sLogMessage (\"Error initializing entity record list\", sLOG_INFO, 0);\n		delete gDBQueryValList;\n		gDBQueryValList = NULL;\n		return sERROR;\n	}\n\n	if( SqlCmd != NULL )\n	{\n		gDBQuerySqlCmd = new char[strlen(SqlCmd)+1];\n		sEditString(gDBQuerySqlCmd, SqlCmd, sNO_TRAILING_SPACES | sNO_LEADING_SPACES);\n	}\n\n	return sSUCCESS;\n}<e>[/code]</e></CODE>\n釋放資料庫\n<CODE><s>[code]</s>int cFreeGlobalDBQueryData( )\n{\n	gDBQueryRowCurrIndex = -1;\n\n	if( gDBQuerySqlCmd != NULL )\n	{\n		delete gDBQuerySqlCmd;\n		gDBQuerySqlCmd = NULL;\n	}\n\n	if( gDBQueryValList == NULL )\n	{\n		return sSUCCESS;\n	}\n\n	sManageList( gDBQueryValList, sLIST_FREE, 0 );\n	delete gDBQueryValList;\n	gDBQueryValList = NULL;\n\n	return sSUCCESS;\n}<e>[/code]</e></CODE>\n查詢SQL Command\n<CODE><s>[code]</s>int cDBQuery( char * SqlCmd, int Mode )\n{\n	cFreeGlobalDBQueryData();\n\n	if( sIsBlank(SqlCmd) != sFALSE )\n	{\n		sLogMessage (\"Blank SQL passed\", sLOG_INFO, 0);\n		return sERROR;\n	}\n\n	if( cAllocGlobalDBQueryData(SqlCmd) != sSUCCESS )\n	{\n		return sERROR;\n	}\n\n	if ( sSUCCESS != sDBValidList( gDBQueryValList, SqlCmd, NULL, NULL, 01|02|Mode ) )\n	{\n		//sLogMessage(\"Error when query the db: %s\", sLOG_INFO, 0, SqlCmd);\n		cFreeGlobalDBQueryData();\n		return sERROR;\n	}\n\n	return sSUCCESS;\n}<e>[/code]</e></CODE>\n抓取下一筆資料\n<CODE><s>[code]</s>char * cGetNextDBQueryRow( )\n{\n	if( gDBQueryValList == NULL || (gDBQueryRowCurrIndex+1) &gt;= gDBQueryValList-&gt;List.ItemsUsed )\n	{\n		return NULL;\n	}\n\n	gDBQueryRowCurrIndex++;\n\n	sVALDATA * valData = (sVALDATA *)sGetListItem( gDBQueryValList, gDBQueryRowCurrIndex );\n\n	if ( !valData )\n	{\n		sLogMessage( \"Error retrieving trade from list\", sLOG_INFO, 0 );\n		return NULL;\n	}\n\n	valData-&gt;Row[sVALDATA_LEN-1] = sDB_SEPARATOR;\n\n	return valData-&gt;Row;\n}<e>[/code]</e></CODE>\n抓取下一欄位\n<CODE><s>[code]</s>char * cGetNextDBQueryRowFieldValue( char * Row, char ** FieldValueStartPtr )\n{\n	static char fieldValueBuff[sMAX_TXT+1];\n\n	if( Row == NULL )\n	{\n		return NULL;\n	}\n\n	fieldValueBuff[0] = \'\\0\';\n	fieldValueBuff[sMAX_TXT] = \'\\0\';\n\n	if( *FieldValueStartPtr == NULL )\n	{\n		*FieldValueStartPtr = Row;\n	}\n\n	*FieldValueStartPtr = sGetValField(fieldValueBuff, *FieldValueStartPtr, sDB_SEPARATOR ); \n\n	sEditString(NULL, fieldValueBuff, sNO_TRAILING_SPACES | sNO_LEADING_SPACES);\n\n	return fieldValueBuff;\n}<e>[/code]</e></CODE>\n\nSample Code\n<CODE><s>[code]</s><i>\n</i>mSTATIC int cAllocGlobalDBQueryData( char * SqlCmd );\n\nstatic sVALLIST * gDBQueryValList      = NULL;\nstatic int	  gDBQueryRowCurrIndex = -1;\nstatic char *	  gDBQuerySqlCmd       = NULL;\n\nchar *getvalue = new char[sTEXT35_LEN];\n	char sqlStr[1024];\n	sprintf(sqlStr, \"select a.AccountID,a.Event,b.book,b.secid,a.valuedate,a.ccy,a.postingdate, \\\n	sum((Case \\\n	when a.EVENT = \'FEE\' Then (case when a.DrOrCr =\'DEBIT\' then a.Amount*-1 else a.Amount end)  \\\n	when a.EVENT = \'P_IAC\' Then (case when a.DrOrCr =\'DEBIT\' then a.Amount*-1 else a.Amount end) \\\n	when a.EVENT = \'P_INT\' Then (case when a.DrOrCr =\'DEBIT\' then a.Amount*-1 else a.Amount end)  \\\n	when a.EVENT = \'cPIAC_BASE\' Then (case when a.DrOrCr =\'DEBIT\' then a.Amount*-1 else a.Amount end) \\\n	when a.EVENT = \'cCD_MTM\' Then (case when a.DrOrCr =\'CREDIT\' then a.Amount*-1 else a.Amount end)  \\\n	end)) Amount \\\n	 from dmpostingevent a left join dmBD_ACCTBOOKPOS b on a.objid = b.id where ACCOUNTID IN (\'295212000\', \'295213000\', \'195212000\', \'195213000\', \'450118007\') and Event in (\'cCD_MTM\', \'P_INT\' , \'FEE\', \'P_IAC\', \'cPIAC_BASE\') \\\n	group by a.AccountID,a.Event,b.book,b.secid,a.valuedate,a.ccy,a.postingdate \\\n	order by  A.Valuedate,b.book,b.secid,a.Event \");\n\n	cAllocGlobalDBQueryData(sqlStr);\n	cDBQuery(sqlStr,00);\n	// 查詢現有的Row\n	char * currentRow = cGetNextDBQueryRow();\n	// 定義資料指標\n	char * fieldValueStartPtr = NULL;\n	cACCT_DETAIL *accDet = new cACCT_DETAIL();\n	memset((char*) accDet, \'\\0\', sizeof(cACCT_DETAIL));\n\n	for (int y = 0; ; y++ )\n	{\n		strcpy(getvalue,cGetNextDBQueryRowFieldValue(currentRow, &amp;fieldValueStartPtr));\n		if(y==0)\n			strcpy(accDet-&gt;AccountID.Name,getvalue);\n		if(y==1)\n			accDet-&gt;Event = (sACCT_EVTYPE)sCIEnum(\"sACCT_EVTYPE\",getvalue,sSHORT_ENUM);\n		if(y==2)\n			strcpy(accDet-&gt;Book.Name,getvalue);\n		if(y==3)\n			strcpy(accDet-&gt;SecId.ID,getvalue);\n		if(y==4)\n			accDet-&gt;ValueDate = sSybIDate(getvalue);\n		if(y==5)\n			strcpy(accDet-&gt;Ccy.Name,getvalue);\n		if(y==6)\n			accDet-&gt;PostingDate = sSybIDate(getvalue);\n		if(y==7)\n			accDet-&gt;Amount = atof(getvalue);\n\n		if( fieldValueStartPtr == NULL )\n		{\n			// Insert Data\n			sInsertListItem((void*)_tradeAcctList, _tradeAcctList-&gt;List.ItemsUsed, (void*) accDet);\n\n			currentRow = cGetNextDBQueryRow();\n			y=-1;	// Reset Count must eq -1 , because start must 0\n			accDet = new cACCT_DETAIL();\n			memset((char*)accDet, \'\\0\', sizeof(cACCT_DETAIL));\n\n			if( currentRow == NULL )\n			{\n				break;\n			}\n		}\n	}\n\n	cFreeGlobalDBQueryData( );\n\n<e>[/code]</e></CODE></r>', 'a4fb042a2fa7b97d37fa638a2a1cca17', 0, 'AIA=', '1ld520tk', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
