(569, 475, 6, 59, 0, '10.48.2.140', 1497580666, 0, 1, 1, 1, 1, '', 'Re: Logger Server介紹', '<r>因為常常會用到Linq，所以我把使用Linq時取得連線字串的內容，Linq轉成SQL語法，以及Linq結果輸出成XML提供如下:<br/>\n詳細內容可參考附件<br/>\n\n<CODE><s>[code]</s><i>\n</i>using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Text;\nusing System.Data.Common;\nusing System.Data.SqlClient;\nusing System.Data;\nusing System.Reflection;\nusing System.IO;\n\nnamespace ConsoleApplication1\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            using (WebTradeDataContext db = new WebTradeDataContext())\n            {\n                var query = from r in db.RTFS_TradeMap\n                            where r.RTFSUser == \"64319\" &amp;&amp; r.Book == \"F_SW\"\n                            select r;\n\n                //取得Linq的查詢命令，並轉為SQL\n                DbCommand dc = db.GetCommand(query);\n                StringBuilder sb = new StringBuilder();\n                sb.Append(string.Format(\"Command Text: {0} \", dc.CommandText.Replace(\"\\r\\n\", \" \")));\n                for (int i = 0; i &lt; dc.Parameters.Count; i++)\n                {\n                    sb.Append(string.Format(\", {0}={1}\", dc.Parameters[i].ParameterName, dc.Parameters[i].Value));\n                }\n\n                //取得Linq的查詢結果，並轉為XML(writer)\n                DataTable dt1 = LinqQueryToDataTable(query);\n                StringWriter writer = new System.IO.StringWriter();\n                dt1.WriteXml(writer, XmlWriteMode.WriteSchema, false);\n                \n                //取得連線字串的內容\n                SqlConnectionStringBuilder builder = new SqlConnectionStringBuilder(dc.Connection.ConnectionString);\n                \n                Console.WriteLine(\"  DB Server--&gt;\" + builder.DataSource);\n                Console.WriteLine(\"DB Instance--&gt;\" + builder.InitialCatalog);\n                Console.WriteLine(\"    DB User--&gt;\" + builder.UserID);\n                Console.WriteLine(\"SQL Command--&gt;\" + sb.ToString());\n                Console.WriteLine(\" SQL Result--&gt;\" + writer);\n\n\n            }\n            Console.ReadKey();\n        }\n\n        public static DataTable LinqQueryToDataTable&lt;T&gt;(IEnumerable&lt;T&gt; varlist)\n        {\n            DataTable dtReturn = new DataTable();\n            PropertyInfo[] props = null;\n            if (varlist == null) return dtReturn;\n            dtReturn.TableName = \"LINQDataTable\";\n\n            foreach (T item in varlist)\n            {\n                if (props == null) //尚未初始化\n                {\n                    Type t = item.GetType();\n                    props = t.GetProperties();\n                    foreach (PropertyInfo pi in props)\n                    {\n                        Type colType = pi.PropertyType;\n                        //針對Nullable&lt;&gt;特別處理\n                        if (colType.IsGenericType &amp;&amp; colType.GetGenericTypeDefinition() == typeof(Nullable&lt;&gt;))\n                            colType = colType.GetGenericArguments()[0];\n                        //建立欄位\n                        dtReturn.Columns.Add(pi.Name, colType);\n                    }\n                }\n                DataRow row = dtReturn.NewRow();\n                foreach (PropertyInfo pi in props)\n                    row[pi.Name] = pi.GetValue(item, null) ?? DBNull.Value;\n                dtReturn.Rows.Add(row);\n            }\n            return dtReturn;\n        }\n    }\n}\n<e>[/code]</e></CODE></r>', 'a8c66a79a18fcb1db2be563bcee800c3', 1, '', '1839yetm', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
(570, 475, 6, 59, 0, '10.48.2.140', 1498441615, 0, 1, 1, 1, 1, '', 'Re: Logger Server介紹', '<r>補上用SQL Command執行時結果轉換為XML的方式<br/>\n其實與Linq兩個方式的重點都是在於轉成DataTable後就能轉為XML<br/>\n\n<CODE><s>[code]</s><i>\n</i>        public static void ConvertSqlCommandToXml()\n        {\n            using (SqlConnection con = new SqlConnection(Properties.Settings.Default.FEIBConnectionString))\n            {\n                try\n                {\n                    con.Open();\n                    SqlCommand cmd = new SqlCommand();\n                    cmd.Connection = con;\n                    cmd.CommandText = \"select * from dbo.BrnoTrades where TradeDate&gt;=@TradeDate and UnisysUserCode=@UnisysUserCode ORDER BY TradeDate\";\n                    cmd.Parameters.Add(\"@TradeDate\", SqlDbType.Date).Value = \"2017-01-01\";\n                    cmd.Parameters.Add(\"@UnisysUserCode\", SqlDbType.Char).Value = \"IVR\";\n                    DataTable wsPrograms = new DataTable();\n                    wsPrograms.TableName = \"BrnoTrades\";\n                    wsPrograms.Load(cmd.ExecuteReader());\n\n                    //取得查詢命令，並轉為TEXT\n                    StringBuilder sb = new StringBuilder();\n                    sb.Append(string.Format(\"Command Text: {0} \", cmd.CommandText.Replace(\"\\r\\n\", \" \")));\n                    for (int i = 0; i &lt; cmd.Parameters.Count; i++)\n                    {\n                        sb.Append(string.Format(\", {0}={1}\", cmd.Parameters[i].ParameterName, cmd.Parameters[i].Value));\n                    }\n\n                    //取得Linq的查詢結果，並轉為XML(writer)\n                    StringWriter writer = new System.IO.StringWriter();\n                    wsPrograms.WriteXml(writer, XmlWriteMode.WriteSchema, false);\n\n                    //取得連線字串的內容\n                    SqlConnectionStringBuilder builder = new SqlConnectionStringBuilder(con.ConnectionString);\n\n                    Console.WriteLine(\"  DB Server--&gt;\" + builder.DataSource);\n                    Console.WriteLine(\"DB Instance--&gt;\" + builder.InitialCatalog);\n                    Console.WriteLine(\"    DB User--&gt;\" + builder.UserID);\n                    Console.WriteLine(\"SQL Command--&gt;\" + sb.ToString());\n                    Console.WriteLine(\" SQL Result--&gt;\" + writer);\n\n                }\n                catch (Exception ex)\n                {\n                    // Handle the error\n                }\n            }\n        }\n<e>[/code]</e></CODE></r>', '0e232086fc3de6d1846705d77e20a765', 0, '', '1e88kqec', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
