(652, 539, 57, 67, 0, '10.48.159.200', 1595268599, 0, 1, 1, 1, 1, '', 'Notes簽核箱', '<r>說明:<br/>\n人資系統會將要簽核表單的資訊呼叫Note WebServices<br/>\n<br/>\nNotes WebService:<br/>\n<URL url=\"http://eip.feib.com.tw/apWEB/WebServi.nsf/wsSyncSignBox?OpenWebService\"><LINK_TEXT text=\"http://eip.feib.com.tw/apWEB/WebServi.n ... WebService\">http://eip.feib.com.tw/apWEB/WebServi.nsf/wsSyncSignBox?OpenWebService</LINK_TEXT></URL><br/>\n<br/>\n/** Call Notes WebService 後所保留的Log **/<br/>\nUSE FEIB_RADAR_M<br/>\nSELECT  [MAILITEM_ID]<br/>\n      ,[PROC_INST_ID]     --表單編號<br/>\n      ,[CREATE_DATE]<br/>\n      ,[NOTES_STRING]     --簽核箱內容<br/>\n      ,[RESULT_STRING]    --傳送狀態(Y:完成)<br/>\n  FROM [FEIB_RADAR_M].[dbo].[SendNotesLog]<br/>\n<br/>\n/**  找出表單相關資訊 **/<br/>\nSELECT TOP (1000) [ROOT_PROC_INST_ID] <br/>\n      ,[PROC_INST_ID] AS 表單編號<br/>\n      ,[FLG_LOCK]<br/>\n      ,[GUID]<br/>\n      ,[PROC_INST_STATE] <br/>\n      ,[PARENT_WKITEM_ID]<br/>\n      ,[PRE_PROC_INST_ID]<br/>\n      ,[PRO_DOMAIN_ID]<br/>\n      ,[PRO_COMP_ID]<br/>\n      ,[PRO_KIND]<br/>\n      ,[PRO_ID]	AS 申請人員<br/>\n      ,[PRO_NAME]<br/>\n      ,[PRO_OU_ID]<br/>\n      ,[PRO_OU_NAME]<br/>\n      ,[PRO_GRP_ID]<br/>\n      ,[PRO_GRP_NAME]<br/>\n      ,[PRO_LIST]<br/>\n      ,[FLG_AGT]<br/>\n      ,[AGT_DOMAIN_ID]<br/>\n      ,[AGT_COMP_ID]<br/>\n      ,[AGT_KIND]<br/>\n      ,[AGT_ID]<br/>\n      ,[AGT_NAME]<br/>\n      ,[AGT_OU_ID]<br/>\n      ,[AGT_OU_NAME]<br/>\n      ,[AGT_GRP_ID]<br/>\n      ,[AGT_GRP_NAME]<br/>\n      ,[CREATE_TIME] AS 發送日期<br/>\n      ,[START_TIME]<br/>\n      ,[COMPLETE_TIME]<br/>\n      ,[PROC_DEF_ID]<br/>\n      ,[PROC_VER]<br/>\n      ,[PROC_STATE]<br/>\n      ,[PROC_NAME] AS 表單名稱<br/>\n      ,[PROC_KIND]<br/>\n      ,[PROC_DESC]<br/>\n      ,[PROC_URL]<br/>\n      ,[AP_ID]<br/>\n      ,[AP_DESC]<br/>\n      ,[EXTEND1] AS 說明1<br/>\n      ,[EXTEND2] AS 說明2<br/>\n      ,[EXTEND3] AS 說明3<br/>\n      ,[EXTEND4]<br/>\n      ,[EXTEND5]<br/>\n  FROM [FEIB_WF20_M_T].[dbo].[PROC_I]<br/>\n<br/>\n/** 寄Mail資訊 **/<br/>\nSELECT TOP (1000) [MAILITEM_ID]<br/>\n      ,[SRC_KIND]<br/>\n      ,[PROC_INST_ID]<br/>\n      ,[WKITEM_ID]<br/>\n      ,[MAIL_STATE]<br/>\n      ,[MAIL_KIND]<br/>\n      ,[FLG_LOCK]<br/>\n      ,[CREATE_TIME]<br/>\n      ,[COMPLETE_TIME]<br/>\n      ,[MAIL_SERVER]<br/>\n      ,[AUTH_MODE]<br/>\n      ,[ID]<br/>\n      ,[PASSWORD]<br/>\n      ,[MAIL_PATH]<br/>\n      ,[FROM_NAME]<br/>\n      ,[FROM_ADDR]<br/>\n      ,[SUBJECT]<br/>\n      ,[BODY]<br/>\n      ,[TO_ADDR]<br/>\n      ,[ATTACH]<br/>\n      ,[CC]<br/>\n      ,[BCC]<br/>\n      ,[BODY_FORMAT]<br/>\n      ,[RETRY_TIMES]<br/>\n      ,[RETRY_COUNT]<br/>\n  FROM [FEIB_WF20_M_T].[dbo].[MAILITEM_I]<br/>\n<br/>\n<br/>\n/** 簽核箱卡單處理(已簽核完成後的表單，不會再簽核箱中消失)，讓表單重送簽核箱，已送過的也沒關係，可重複執行。 **/<br/>\nUSE FEIB_RADAR_M<br/>\n<br/>\nBEGIN TRAN<br/>\n<br/>\nDECLARE @date DATETIME = \'2020-07-16 00:00:00\' --開始發送失敗時間<br/>\n<br/>\n--確認資料<br/>\nSELECT *<br/>\nFROM dbo.SendNotesLog WHERE CREATE_DATE &gt;= @date AND ISNULL(RESULT_STRING,\'\') = \'\'<br/>\n<br/>\ndelete <br/>\nFROM dbo.SendNotesLog WHERE CREATE_DATE &gt;= @date AND ISNULL(RESULT_STRING,\'\') = \'\'<br/>\n<br/>\n--沒問題改commit<br/>\n--commit<br/>\nROLLBACK<br/>\n<br/>\n\n<ATTACHMENT filename=\"簽核箱資料交換_20190415.xls\" index=\"0\"><s>[attachment=0]</s>簽核箱資料交換_20190415.xls<e>[/attachment]</e></ATTACHMENT></r>', 'c1649b8b5bfde2d0d4c7130a223a8988', 1, '', '19t67skm', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
