(700, 302, 26, 58, 0, '10.48.1.120', 1632512236, 0, 1, 1, 1, 1, '', 'Re: FETP DB 亂碼去識別化', '<r><B><s>[b]</s>DB 資料亂碼化參考 Script：<e>[/b]</e></B>\n<CODE><s>[code]</s><i>\n</i>--UserLogin \nupdate UserLogin set realName = SUBSTRING(realName,1,1)+\'*\'+SUBSTRING(realName,3,LEN(realName)-2)\nupdate UserLogin set callemail = \'***\'+SUBSTRING(callemail,3,LEN(callemail)-2) where CHARINDEX(\'@\', callemail) &gt; 0\n--select * from Margin.dbo.UserLogin\nupdate AccountCheckBalance set accName = SUBSTRING(accName,1,1)+\'*\'+SUBSTRING(accName,3,LEN(accName)-2) where LEN(accName)&gt;2\n\n--UserInfo \nupdate UserInfo set tel1 = SUBSTRING(tel1,1,LEN(tel1) - 4)+\'****\' where LEN(tel1) &gt;= 8\nupdate UserInfo set tel2 = SUBSTRING(tel2,1,LEN(tel2) - 4)+\'****\' where LEN(tel2) &gt;= 8\nupdate UserInfo set ladr = SUBSTRING(ladr,1,8)+\'******\' where LEN(ladr) &gt;= 8\nupdate UserInfo set cadr = SUBSTRING(cadr,1,8)+\'******\' where LEN(cadr) &gt;= 8\nupdate UserInfo set pacadr = SUBSTRING(pacadr,1,8)+\'******\' where LEN(pacadr) &gt;= 8\nupdate UserInfo set NAME = SUBSTRING(NAME,1,1)+\'**\'+SUBSTRING(NAME,3,LEN(NAME)-2) where LEN(NAME) &gt; 1\nupdate UserInfo set email2 = \'***\'+SUBSTRING(email2,3,LEN(email2)-2) where CHARINDEX(\'@\', email2) &gt; 0\n--select * from UserInfo\n\n--AccountStrategy \nupdate AccountStrategy set acname = SUBSTRING(acname,1,1)+\'*\'+SUBSTRING(acname,3,LEN(acname)-2) where LEN(acname) &gt; 1\nupdate AccountStrategy set realName = SUBSTRING(realName,1,1)+\'*\'+SUBSTRING(realName,3,LEN(realName)-2) where LEN(realName) &gt; 1\n--select * from AccountStrategy\n\n-- Setting Phone Pin (Service Restart Required!!)\ninsert INTO UNISYS_READ_PHONEPIN\nSELECT  a.account      AS [ACTNO],\n        \'123456\'       AS [TELPIN], -- 電話密碼\n        GETDATE()      AS [TXTIME],\n        a.Aeid         AS [UNINO],\n        null           as [AGBRNO]\nFROM    AccountStrategy as a\n--select * from UNISYS_READ_PHONEPIN  order by txtime desc\n\n-- Setting Net Pin (Service Restart Required!!)\ninsert INTO unisys_read_loginpass\nSELECT  a.Aeid        AS [UNINO],\n        \'12345678\'    AS [NETPIN], -- 網路密碼      \n        GETDATE()     AS [TXTIME],\n        a.account     AS [ACTNO],\n        null          AS [AGBRNO]\nFROM    AccountStrategy as a\n--select * from unisys_read_loginpass order by txtime desc\n\n--Copy_UserLogin (Closed Account)\nupdate copy_UserLogin set realName = SUBSTRING(realName,1,1)+\'*\'+SUBSTRING(realName,3,LEN(realName)-2)\nupdate copy_UserLogin set callemail = \'***\'+SUBSTRING(callemail,3,LEN(callemail)-2)\n--select * from copy_UserLogin\n\n--UserInfo (Closed Account)\nupdate copy_UserInfo set tel1 = SUBSTRING(tel1,1,LEN(tel1) - 4)+\'****\' where LEN(tel1) &gt;= 8\nupdate copy_UserInfo set tel2 = SUBSTRING(tel2,1,LEN(tel2) - 4)+\'****\' where LEN(tel2) &gt;= 8\nupdate copy_UserInfo set ladr = SUBSTRING(ladr,1,8)+\'******\' where LEN(ladr) &gt;= 8\nupdate copy_UserInfo set cadr = SUBSTRING(cadr,1,8)+\'******\' where LEN(cadr) &gt;= 8\nupdate copy_UserInfo set pacadr = SUBSTRING(pacadr,1,8)+\'******\' where LEN(pacadr) &gt;= 8\nupdate copy_UserInfo set NAME = SUBSTRING(NAME,1,1)+\'**\'+SUBSTRING(NAME,3,LEN(NAME)-2) where LEN(NAME) &gt; 1\n--select * from copy_UserInfo\n\n--AccountStrategy Closed\nupdate copy_AccountStrategy set acname = SUBSTRING(acname,1,1)+\'*\'+SUBSTRING(acname,3,LEN(acname)-2) where LEN(acname) &gt; 1\nupdate copy_AccountStrategy set realName = SUBSTRING(realName,1,1)+\'*\'+SUBSTRING(realName,3,LEN(realName)-2) where LEN(realName) &gt; 1\n--select * from copy_AccountStrategy\n\n-- UserMarginRecord\nupdate UserMarginRecord set CntctNm = SUBSTRING(CntctNm,1,1)+\'*\'+SUBSTRING(CntctNm,3,LEN(CntctNm)-2) where LEN(CntctNm) &gt; 1\nupdate UserMarginRecord set CntctTel = Left(CntctTel, 4) +\'**********\' where LEN(CntctTel) &gt;= 8\n\n-- Client UserName\nupdate userLogin set userName = null\n\n-- AccountCheckBalance\nupdate AccountCheckBalance set accName = SUBSTRING(accName,1,1)+\'*\'+SUBSTRING(accName,3,LEN(accName)-2) where LEN(accName)&gt;2\nupdate AccountCheckBalance set accName = SUBSTRING(accName,1,1)+\'*\' where LEN(accName)=2\n\n-- AccountFirstTradeRecord\nupdate AccountFirstTradeRecord set accName = SUBSTRING(accName,1,1)+\'*\'+SUBSTRING(accName,3,LEN(accName)-2) where LEN(accName)&gt;2\nupdate AccountFirstTradeRecord set accName = SUBSTRING(accName,1,1)+\'*\' where LEN(accName)=2\n\n-- BusinessTransactionDetails\nupdate BusinessTransactionDetails set Name = SUBSTRING(Name,1,1)+\'*\'+SUBSTRING(Name,3,LEN(Name)-2) where LEN(Name)&gt;2\nupdate BusinessTransactionDetails set Name = SUBSTRING(Name,1,1)+\'*\' where LEN(Name)=2\n\n-- CaRecord\nupdate CaRecord set realName = SUBSTRING(realName,1,1)+\'*\'+SUBSTRING(realName,3,LEN(realName)-2) where LEN(realName)&gt;2\nupdate CaRecord set realName = SUBSTRING(realName,1,1)+\'*\' where LEN(realName)=2\n\n-- copy_UserLogin\nupdate copy_UserLogin set realName = SUBSTRING(realName,1,1)+\'*\'+SUBSTRING(realName,3,LEN(realName)-2) where LEN(realName)&gt;2\nupdate copy_UserLogin set realName = SUBSTRING(realName,1,1)+\'*\' where LEN(realName)=2\n\n-- UserMarginRecordHis\nupdate UserMarginRecordHis set CntctNm = SUBSTRING(CntctNm,1,1)+\'*\'+SUBSTRING(CntctNm,3,LEN(CntctNm)-2) where LEN(CntctNm)&gt;2\nupdate UserMarginRecordHis set CntctNm = SUBSTRING(CntctNm,1,1)+\'*\' where LEN(CntctNm)=2\n\n-- xCCIF\nupdate xCCIF set cntct_tel_h = SUBSTRING(cntct_tel_h,1,LEN(cntct_tel_h) - 4)+\'****\' where LEN(cntct_tel_h) &gt;= 8\nupdate xCCIF set cntct_tel = SUBSTRING(cntct_tel,1,LEN(cntct_tel) - 4)+\'****\' where LEN(cntct_tel) &gt;= 8\nupdate xCCIF set addr1 = SUBSTRING(addr1,1,8)+\'******\' where LEN(addr1) &gt;= 8\nupdate xCCIF set addr2 = SUBSTRING(addr2,1,8)+\'******\' where LEN(addr2) &gt;= 8\nupdate xCCIF set addr3 = SUBSTRING(addr3,1,8)+\'******\' where LEN(addr3) &gt;= 8\nupdate xCCIF set cntct_nm = SUBSTRING(cntct_nm,1,1)+\'**\'+SUBSTRING(cntct_nm,3,LEN(cntct_nm)-2) where LEN(cntct_nm) &gt; 1\nupdate xCCIF set email_addr = \'***\'+SUBSTRING(email_addr,3,LEN(email_addr)-2) where CHARINDEX(\'@\', email_addr) &gt; 0\n\n-- xCIF\nupdate xCIF set cntct_tel_h = SUBSTRING(cntct_tel_h,1,LEN(cntct_tel_h) - 4)+\'****\' where LEN(cntct_tel_h) &gt;= 8\nupdate xCIF set cntct_tel = SUBSTRING(cntct_tel,1,LEN(cntct_tel) - 4)+\'****\' where LEN(cntct_tel) &gt;= 8\nupdate xCIF set addr1 = SUBSTRING(addr1,1,8)+\'******\' where LEN(addr1) &gt;= 8\nupdate xCIF set addr2 = SUBSTRING(addr2,1,8)+\'******\' where LEN(addr2) &gt;= 8\nupdate xCIF set addr3 = SUBSTRING(addr3,1,8)+\'******\' where LEN(addr3) &gt;= 8\nupdate xCIF set cntct_nm = SUBSTRING(cntct_nm,1,1)+\'**\'+SUBSTRING(cntct_nm,3,LEN(cntct_nm)-2) where LEN(cntct_nm) &gt; 1\nupdate xCIF set email_addr = \'***\'+SUBSTRING(email_addr,3,LEN(email_addr)-2) where CHARINDEX(\'@\', email_addr) &gt; 0\n\n--Clear Mobile DeviceToken\ntruncate table DeviceToken\n\n--MarginDemo\nupdate FETPDEMO set RESIDENCEADDRESS = SUBSTRING(RESIDENCEADDRESS,1,8)+\'******\' where LEN(RESIDENCEADDRESS) &gt;= 8\nupdate FETPDEMO set MAILINGADDRESS = SUBSTRING(MAILINGADDRESS,1,8)+\'******\' where LEN(MAILINGADDRESS) &gt;= 8\nupdate FETPDEMO set NAME = SUBSTRING(NAME,1,1)+\'*\'+SUBSTRING(NAME,3,LEN(NAME)-2) where LEN(NAME)&gt;2\nupdate FETPDEMO set PID = SUBSTRING(PID,1,1)+\'*\'+SUBSTRING(PID,3,LEN(PID)-2) where LEN(PID)&gt;2\nupdate FETPDEMO set TELEPHONENUMBER = SUBSTRING(TELEPHONENUMBER,1,LEN(TELEPHONENUMBER) - 4)+\'****\' where LEN(TELEPHONENUMBER) &gt;= 8\nupdate FETPDEMO set PHONENUMBER = SUBSTRING(PHONENUMBER,1,LEN(PHONENUMBER) - 4)+\'****\' where LEN(PHONENUMBER) &gt;= 8\nupdate ACTIVITEREPORT set NAME = SUBSTRING(NAME,1,1)+\'*\'+SUBSTRING(NAME,3,LEN(NAME)-2) where LEN(NAME)&gt;2\nupdate ACTIVITEREPORT set TEL = SUBSTRING(TEL,1,LEN(TEL) - 4)+\'****\' where LEN(TEL) &gt;= 8\nupdate ACTIVITEREPORT set MOBILE = SUBSTRING(MOBILE,1,LEN(MOBILE) - 4)+\'****\' where LEN(MOBILE) &gt;= 8    \nupdate ACTIVITEREPORT set ADDRESS = SUBSTRING(ADDRESS,1,8)+\'******\' where LEN(ADDRESS) &gt;= 8\nupdate ACTIVITEREPORT set EMAIL = \'***\'+SUBSTRING(EMAIL,3,LEN(EMAIL)-2) where CHARINDEX(\'@\', EMAIL) &gt; 0\n<e>[/code]</e></CODE></r>', '3e2c554e21f8500836ec947bc3d974a2', 0, '', '1uj52wwn', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
