(780, 612, 26, 58, 0, '10.48.1.120', 1685435698, 0, 1, 1, 1, 1, '', 'FETP 變更系統統編或身份證字號 sql script', '<r>變更系統統編或身份證字號\n<CODE><s>[code]</s><i>\n</i>--例)\'W200193684\'-&gt;取代為-&gt;\'N200193684\'\nselect	* into	#userlogin_temp from	UserLogin where aeid=\'W200193684\';\nGO\nupdate #userlogin_temp set aeid=\'N200193684\' ;\nGO\ninsert into UserLogin  select * from #userlogin_temp;\nGO\ndrop table #userlogin_temp;\nGO\nupdate AccountStrategy set aeid =\'N200193684\' where aeid=\'W200193684\';\nGO\nupdate UserInfo set aeid =\'N200193684\' where aeid=\'W200193684\';\nGO\ndelete from UserLogin where aeid=\'W200193684\';\nGO\nupdate UserPassword set aeid =\'N200193684\' where aeid=\'W200193684\';\nGO\nupdate EODAccountSummary set aeid =\'N200193684\' where aeid=\'W200193684\';\nGO\nupdate Message2AccountIDMap set aeid =\'N200193684\' where aeid=\'W200193684\';\nGO\nupdate BusinessTransactionDetails set aeid =\'N200193684\' where aeid=\'W200193684\';\nGO\nupdate MiddleRecordUserMaxTrade set aeid =\'N200193684\' where aeid=\'W200193684\';\nGO\nupdate userlogin set userName = null, caLimitNumber = caLimitNumber+2 where  aeid=\'N200193684\' ;\nGO\nUpdate torderhis set openusername =\'N200193684\'  where openusername =\'W200193684\'\nUpdate torderhis set closeusername =\'N200193684\'  where closeusername =\'W200193684\'\nGO\n<e>[/code]</e></CODE></r>', '965026e49e80e418349e4a65feedf022', 0, '', 'xh6uz62a', 1, 0, '', 0, 0, 0, 1, 0, '', 0),
